import{_ as a,C as t,c as l,o as u,a1 as h,j as i,G as c,w as m,a as g}from"./chunks/framework.DhXu_kq-.js";const C=JSON.parse('{"title":"Milthm story text","description":"","frontmatter":{},"headers":[],"relativePath":"vi.md","filePath":"vi.md"}'),o={name:"vi.md"};function y(v,n,k,s,p,q){const r=t("pause"),b=t("shake");return u(),l("div",null,[n[4]||(n[4]=h('<h1 id="milthm-story-text" tabindex="-1">Milthm story text <a class="header-anchor" href="#milthm-story-text" aria-label="Permalink to &quot;Milthm story text&quot;">​</a></h1><h2 id="contents" tabindex="-1">Contents <a id="contents"></a> <a class="header-anchor" href="#contents" aria-label="Permalink to &quot;Contents &lt;a id=&quot;contents&quot;&gt;&lt;/a&gt;&quot;">​</a></h2><ul><li><a href="#story">Cốt truyện chính</a><ul><li><a href="#chapter0">Khởi đầu: Thanh âm của mưa</a></li><li><a href="#chapter1">CHƯƠNG 1: Hai mặt Đắng Cay và Ngọt Ngào</a></li></ul></li><li><a href="#side">Ngoại Truyện 1</a><ul><li><a href="#side1">Xuân Chờ Trong Hoa</a></li></ul></li><li><a href="#Collaboration">Hợp tác</a><ul><li><a href="#rainworld">Rain World</a></li><li><a href="#notanote">Notanote</a></li></ul></li></ul><hr><h2 id="cot-truyen-chinh" tabindex="-1">Cốt truyện chính <a id="story"></a> <a class="header-anchor" href="#cot-truyen-chinh" aria-label="Permalink to &quot;Cốt truyện chính &lt;a id=&quot;story&quot;&gt;&lt;/a&gt;&quot;">​</a></h2><ul><li><a href="#contents">Contents</a></li></ul><h3 id="khoi-đau-thanh-am-cua-mua" tabindex="-1">Khởi đầu: Thanh âm của mưa <a id="chapter0"></a> <a class="header-anchor" href="#khoi-đau-thanh-am-cua-mua" aria-label="Permalink to &quot;Khởi đầu: Thanh âm của mưa &lt;a id=&quot;chapter0&quot;&gt;&lt;/a&gt;&quot;">​</a></h3><p>Lách, tách.<br><br><br><br><br><br>Lách, tách, lách, tách…<br><br><br><br><br><br>Thành phố lúc bấy giờ đắm chìm trong nhịp điệu của cơn mưa.<br><br><br><br><br><br>Cơn mưa ấy mãi không ngừng rơi.<br><br><br><br><br><br>Tôi đứng dưới mái hiên cầu thang, vô số những hạt mưa rơi lụp bụp trên đất, tạo nên những tiếng róc rách nghe tựa như những bản giao hưởng vĩnh hằng.<br><br><br><br><br><br>Cơn mưa đã luôn rơi không ngừng từ trước khi tôi được sinh ra. Nghe các cụ kể lại, rằng cơn mưa đã có từ trước cả khi họ đến với thế giới này.<br><br><br><br><br><br>Mưa rơi không ngừng nghỉ. Rơi một cách vô tư, chạm tới mọi nơi trên mặt đất này, mang đi những ai quan tâm đến chúng, dẫn họ vào cõi mưa. Với những ai nhìn thấy họ, trông họ dường như đang chìm sâu vào giấc ngủ, bị giam cầm vào giấc mơ vô tận.<br><br><br><br><br><br>Mọi người tìm nơi trú ẩn, trong những ngôi nhà bằng bê tông cốt thép vững chãi, mặc quần áo vải ni lông dày có khả năng chống thấm nước - tất cả đều nhằm mục đích ngăn chặn việc đi lạc vào giấc mơ vĩnh hằng ấy.<br><br><br><br><br><br>Tôi quan sát họ, những người sống trong nỗi sợ một ngày sẽ bị cơn mưa ấy nhấn chìm, sống trong một vòng lặp vô vị mỗi ngày: tìm kiếm thức ăn, may vá quần áo, và bảo trì cho ngôi nhà của họ…<br><br><br><br><br><br>Tôi chẳng thể nào biết được liệu thế giới trong mơ có hạnh phúc hơn thế giới thực hay không. Tôi chỉ lặng lẽ nghe những lời nói của mưa. Rộn ràng, vang vọng và đôi khi gầm lên một tiếng sấm. Tôi vẫn lặng im lắng nghe.<br><br><br><br><br><br>Tách, tách, tách…<br><br><br><br><br><br>Cơn mưa nói như vậy đấy. Dì Susan là hàng xóm láng giềng của tôi vừa mới sinh con.<br><br><br><br><br><br>Mọi người đều rất vui mừng quây quần bên cạnh cô ấy, để chúc mừng cho sự chào đời của em bé.<br><br><br><br><br><br>Ngay lúc này, dì Susan dường như đã trở thành <i>Đức Trinh Nữ Mary</i>. Mồ hôi và máu trên cơ thể cô ấy mang một năng lượng thần thánh, trở thành phép màu biểu tượng của sự đau đớn.<br><br><br><br><br><br>Bữa tiệc bắt đầu. Trong tòa nhà có lẽ cách mặt đất nơi đây hơn 100 mét trên mặt đất. Mọi người mang đến những món quà và thức ăn ngon nhất mà họ tìm được và để chúng bên cạnh chiếc giường kim loại của Susan. Cô ấy nhìn một cách yếu ớt vào những thanh sô cô la, thịt khô và quần áo - những loại quần áo không phải để bảo vệ mà chỉ để cho đẹp - mà những thứ đó chỉ có thể tìm thấy trên mặt đất. Một nụ cười tươi nở trên khuôn mặt cô.<br><br><br><br><br><br>Trong bữa tối, không hề có một chút nấm nào ở đây. Ở chỗ họ, có một nồi súp thịt thịnh soạn, được chế biến từ nhiều loại rau củ và gia vị không rõ là gì. Mùi thơm của súp tỏa ra khắp nhà, đắm chìm vào những hương thơm tuyệt vời, xua tan mọi mùi ẩm mốc trong phòng.<br><br><br><br><br><br>Từng thìa từng thìa một, món súp nóng hổi làm ấm người và xua tan mọi muộn phiền trong lòng. Chẳng mấy chốc, nồi súp đã hết sạch. Mọi người quây quần bên nhau cạnh đống lửa, nhâm nhi một loại rượu đặc biệt tự làm - là rượu nấm - trong khi thản nhiên trò chuyện về những điều chỉ xảy ra trong giấc mơ.<br><br><br><br><br><br>Ngọn lửa cuối cùng cũng tắt, và mọi người đều ra về cả.<br><br><br><br><br><br>Nhưng còn Susan thì vẫn chưa được nếm món súp tí nào. Niềm vui mà bữa tiệc mang lại đang dần tiêu tan.<br><br><br><br><br><br>Mọi người quay lại cuộc sống thường ngày : Đi kiếm ăn, may vá quần áo, và sửa soạn cho ngôi nhà của họ …<br><br><br><br><br><br>Để mà nói là có gì khác, thì có lẽ là trong bữa ăn ấy, họ chỉ có thể thưởng thức vài miếng thịt khô và rượu nấm thừa còn sót lại sau bữa tiệc.<br><br><br><br><br><br>Susan vẫn đang nghỉ ngơi hồi phục trên giường, trông có vẻ vẫn ủ rũ sau khi sinh. Con của dì ấy đã được bế vào trong căn phòng khác, đặt nằm trong một chiếc cũi gỗ. Bởi vì Susan quá yếu, nên họ cố gắng kiếm một ít sữa bột cho em bé uống.<br><br><br><br><br><br>Em bé thì chưa được đặt tên. Nó bú bình sữa, toát lên một ánh mắt tràn ngập sự ngây thơ.<br><br><br><br><br><br>Chồng của dì Susan, sau khi vui mừng vì đã lên chức bố, giờ đây lại có nhiều nỗi lo đè nặng. Có thêm thành viên mới tức là sẽ có thêm một người ăn. Mặc dù bây giờ họ có thể tự nuôi sống bản thân thông qua việc cho con bú, nhưng rồi trong tương lai họ có thể kiếm ăn ở đâu ?<br><br><br><br><br><br>Người chồng đi lại luẩn quẩn trong căn phòng chật chội, đột nhiên nhớ tới bên trên mặt đất họ đang chế tạo một số loại máy móc và họ cần tuyển thêm nhân lực. Có tin đồn nói rằng việc đăng ký vào đó sẽ cho phép họ mang theo một người đồng hành và di chuyển lên trên đó để sinh sống.<br><br><br><br><br><br>Chồng và Ông của họ ngồi trầm tĩnh trên ghế, đầu óc mông lung. Người bố nhìn đứa bé đang nằm ngủ một cách yên bình trên giường. Suy nghĩ một lúc, rồi lại nghĩ lại lần nữa trước khi đưa ra quyết định và đứng dậy và đi về phía vợ mình, nói điều gì đó.<br><br><br><br><br><br>Khuôn mặt cô ấy nhăn nhó vì đau đớn, nhưng gật đầu đồng ý.<br><br><br><br><br><br>Ngày hôm sau, cả em bé và người chồng cùng rời đi. Một tuần sau, Susan giờ đây đã có thể đi được lên mặt đất.<br><br><br><br><br><br>Mặc dù trông dì ấy vẫn còn hơi yếu đuối, nhưng trên khuôn mặt cô vẫn luôn nở nụ cười hiền hậu. Khi mọi người thấy cô, mặt họ trông có vẻ lúng túng, như thể có điều gì muốn nói, nhưng cuối cùng, họ cũng vẫn chỉ lắc đầu và bỏ đi.<br><br><br><br><br><br>Susan muốn góp sức làm một việc gì đó, thế nhưng mọi người, vì tốt bụng, mà nhất quyết buộc cô phải bình tĩnh lại đã, thật là đáng tiếc cho người đã mất cả gia đình. Cô vẫn cố giữ nụ cười với lòng tốt của họ, và một dấu vết đau buồn khó thấy hiện lên, rồi lại nhanh chóng bị nụ cười che đậy.<br><br><br><br><br><br>Một tuần nữa lại trôi qua, và vẫn vậy, không một ai chịu để dì Susan làm bất cứ việc gì, và nụ cười của cô dần ít xuất hiện hơn.<br><br><br><br><br><br>Nhiều lúc, bạn có thể thấy cô ấy ở một góc phòng, mặt quay vào tường, cơ thể dường như đang run rẩy. Khi cô quay mặt lại, nụ cười ấy vẫn hiện trên khuôn mặt như thường ngày, nhưng đôi mắt sưng tấy và những vệt nước mắt đã nói lên tất cả.<br><br><br><br><br><br>Bỗng một ngày, Susan biến mất, và không ai có thể tìm được dì ấy. Mọi người kiếm tìm khắp nơi và rồi tìm thấy dì ấy ở trên sân thượng. Cô nằm ở đó dưới cơn mưa rào, trên môi vẫn nở một nụ cười dịu dàng. Mọi người mặc bộ quần áo mưa dày đặc để mang Susan trở về.<br><br><br><br><br><br>Họ chỉ cách dì ấy có vài mét, nhưng vẫn phải mặc đồ bảo hộ. Cảm giác như địa ngục chỉ cách họ có vài bước chân.<br><br><br><br><br><br>Mọi người trông đều nghiêm nghị, trong khi gương mặt Susan vẫn mang nụ cười đó. Họ đưa vào một khu cách ly, nối cho cô ấy một thiết bị giúp hỗ trợ sự sống, và rồi họ thở dài, rời khỏi căn phòng.<br><br><br><br><br><br>Thiết bị đó kêu bíp một cách nhịp nhàng, như thể dì chỉ vừa mới chìm vào một giấc ngủ yên bình.<br><br><br><br><br><br>Ngoài trời vẫn còn mưa. Mãi không bao giờ dừng lại. Dù cho có đưa đi thêm ai đó, nhịp điệu cơn mưa ấy vẫn không thay đổi.<br><br><br><br><br><br>Bíp. Bíp. Bíp.<br><br><br><br><br><br>Tôi đến bên cạnh giường, nhìn ngực dì cứ chầm chậm to lên rồi lại hạ xuống . Trên khuôn mặt dì ấy mang theo một vẻ êm đềm và vui tươi. Chắc hẳn dì phải có một giấc mơ rất đẹp.<br><br><br><br><br><br>Dì ấy mơ cái gì vậy nhỉ? Tôi cảm thấy rất tò mò.<br><br><br><br><br><br>Theo như những gì tôi nhớ, giấc mơ của tôi chả có gì ngoài bóng tối. Có khi còn chẳng phải gọi là &quot;giấc mơ&quot;, chỉ giống như ai đó vẽ nên một bức tranh thô thiển về giấc mơ bằng một màu đen xì.<br><br><br><br><br><br>Tôi muốn đưa tay ra và chạm vào dì ấy, nhưng bị người khác gọi ra. Họ sợ rằng căn bệnh này có thể lây lan.<br><br><br><br><br><br>Buổi tối, mọi người quây quần bên đống lửa trại và thì thầm về Susan. Một số người nói rằng cô ấy nên được đưa lên mặt đất, một số người lại nói rằng cô ấy nên ở lại đây, và thậm chí một số còn đề nghị tắt thiết bị hỗ trợ sự sống. Nhưng họ cãi nhau, rồi cuối cùng, không đưa ra được quyết định nào. Họ thầm mong thiết bị sẽ tự dừng lại để không bị phải chịu bất kỳ trách nhiệm nào.<br><br><br><br><br><br>Đêm nay, tôi vẫn ngủ không mơ. Có lẽ những giấc mơ đã tách khỏi tôi.<br><br><br><br><br><br>Tôi bật dậy và đi đến chỗ Susan, ngắm nhìn dì ấy, tưởng tượng ra giấc mơ của cô.<br><br><br><br><br><br>Rồi bỗng tôi bị lạc. Khi tôi tỉnh lại, thì đang đứng trên sân thượng. Mưa rơi vào mặt, bắn tung tóe bắn tùm lum trên mặt đất, tạo nên những âm thanh róc rách.<br><br><br><br><br><br>Bíp. Bíp. Bíp.<br><br><br><br><br><br>Tôi bước đi về phía trước. Tất cả đều đen ngòm. Một màu đen sâu thẳm.<br><br><br><br><br><br>Một thế giới không còn cơn mưa.<br><br><br><br><br><br>Không còn tiếng mưa rơi lộp độp trên những đồ vật, không còn tiếng nước văng tung tóe.<br><br><br><br><br><br>Vẫn vậy. Bình Yên. Tĩnh lặng.<br><br><br><br><br><br>Không còn tiếng nổ lách tách, không còn tiếng nói chuyện không ngớt, không còn thiết bị phát ra tiếng bíp.<br><br><br><br><br><br>Một sự im lặng nói lên mọi thứ.<br><br><br><br><br><br>Vậy ra, đây là giấc mơ của tôi. Trùng hợp đến bất ngờ.<br><br><br><br><br><br>Nhưng… khi đã quen với cơn mưa, quen với tiếng con người, sự thiếu vắng của tất cả khiến tôi cảm thấy có chút cô đơn.<br><br><br><br><br><br>&quot;Cậu đang thấy cô đơn ư?&quot; một giọng nói vang lên, nhẹ nhàng và dịu êm.<br><br><br><br><br><br>&quot;Ngươi là ai ?&quot; Tôi hỏi.<br><br><br><br><br><br>&quot;Mình chính là cậu, là chính cậu nhưng mà không thể nhìn thấy.&quot;<br><br><br><br><br><br>Tôi chợt nhận ra đó là giọng nói của tôi.<br><br><br><br><br><br>&quot;Cậu là… tôi?&quot;<br><br><br><br><br><br>&quot;Đúng vậy, chúng mình giống như hai mặt của một đồng tiền. Mình xuất hiện vì bạn, như những gì bạn nghĩ. Tớ biết cậu nghĩ gì. Và tớ biết cậu muốn gì.&quot;<br><br><br><br><br><br>&quot;Tớ muốn… chính xác là tớ muốn gì ?&quot;<br><br><br><br><br><br>&quot;Hãy hướng về phía ánh sáng. Cậu sẽ tìm thấy câu trả lời ở đó thôi.&quot;<br><br><br><br><br><br>Rồi giọng nói biến mất.<br><br><br><br><br><br>Một tia sáng nhỏ lóe lên ở phía xa, và tôi bước về phía đó.<br><br><br><br><br><br>…<br><br><br><br><br><br>Tôi bước đi một cách nhẹ nhàng, mặt đất tựa bông hồng, không gây ra chút tiếng động nào, không biết đã đi được bao xa. Chân tôi đau nhức, bắp chân tôi mỏi nhừ, hơi thở nặng nề. Ánh sáng phía trước dường như không có gần hơn.<br><br><br><br><br><br>Tôi vẫn cố bước đi, cổ họng khô rát, mắt mờ dần, và thậm chí không ngẩng đầu lên nổi. Tôi vẫn bước tiếp.<br><br><br><br><br><br>Cảm giác tê nhức ở chân đột nhiên thấy ấm áp. Tôi cố gắng ngẩng đầu lên. Một quả cầu lửa khổng lồ bay lơ lửng trước mặt tôi.<br><br><br><br><br><br>&quot;Chạm vào nó đi&quot; Một ý nghĩ xuất hiện trong tôi và tôi làm theo.<br><br><br><br><br><br>Quả cầu lửa không hề làm tôi bỏng; thay vào đó, nó nhẹ nhàng nắm trọn tay tôi.<br><br><br><br><br><br>Hơi ấm dâng trào trong tôi, đi kèm với cảm xúc.<br><br><br><br><br><br>Tôi tìm kiếm chúng, liều lĩnh lao vào quả cầu lửa cho đến khi toàn bộ cơ thể tôi bị nhấn chìm vào trong đó.<br><br><br><br><br><br>Ánh sáng rực rỡ bao trùm lấy tôi, những dòng chảy ấm áp ôm chặt lấy tôi, cảm xúc của tôi bị cuốn theo, vẽ vào bóng tối bằng vô số sắc màu.<br><br><br><br><br><br>Niềm vui, nỗi đau, hạnh phúc, nỗi buồn, sự mãn nguyện, thất vọng, ngưỡng mộ, ghen tị…<br><br><br><br><br><br>Trong ánh sáng chói lóa, ý thức của tôi lại bị gián đoạn một lần nữa.<br><br><br><br><br><br>…<br><br><br><br><br><br>Tiếng mưa rơi.<br><br><br><br><br><br>Những giọt mưa rơi trên má tôi, tạo thành tiếng lộp độp.<br><br><br><br><br><br>Mở mắt ra; tôi đang đứng trên sân thượng. Ắt hẳn tôi vừa quay trở về từ giấc mơ.<br><br><br><br><br><br>Tôi đứng dậy, nhìn mưa rơi, để nó chảy trên người tôi.<br><br><br><br><br><br>Ôi thật bất ngờ, tôi cảm thấy rất thoải mái, thậm chí là rất vui. Tôi bật lên tiếng cười nhẹ.<br><br><br><br><br><br>&quot;Cậu là ai vậy? Và đây là đâu?&quot;<br><br><br><br><br><br>Giọng nói của một cô gái vang lên phía sau tôi.<br><br><br><br><br><br>Tôi quay lại. Một cô gái trẻ trong bộ váy trắng đứng trước mặt tôi. Những giọt mưa rơi trên người cô, nhưng chúng không làm ướt váy hoặc làm ướt mái tóc trắng bồng bềnh của cô.<br><br><br><br><br><br>Đôi mắt xanh nhạt của cô ấy nhìn chằm chằm vào tôi.<br><br><br><br><br><br>&quot;Tại sao cậu lại khóc vậy?&quot;<br><br><br><br><br><br>Cô ấy hỏi.<br><br><br><br></p><h3 id="chuong-1-hai-mat-đang-cay-va-ngot-ngao" tabindex="-1">CHƯƠNG 1: Hai mặt Đắng Cay và Ngọt Ngào <a id="chapter1"></a> <a class="header-anchor" href="#chuong-1-hai-mat-đang-cay-va-ngot-ngao" aria-label="Permalink to &quot;CHƯƠNG 1: Hai mặt Đắng Cay và Ngọt Ngào &lt;a id=&quot;chapter1&quot;&gt;&lt;/a&gt;&quot;">​</a></h3><ul><li><a href="#contents">Contents</a></li></ul><h4 id="_1-1" tabindex="-1">1-1 <a class="header-anchor" href="#_1-1" aria-label="Permalink to &quot;1-1&quot;">​</a></h4><ul><li>…</li><li>Tại sao bạn lại khóc vậy ?</li><li>(Đứng trước mặt tôi, cô gái nhìn với vẻ mặt vô tri.)</li><li>Ể ?</li><li>Bạn đang khóc kìa. Tại sao vậy ?</li><li>(Tôi không hề nhận ra có một hơi ấm mờ nhạt trên gò má tôi cho đến khi cô ấy nhắc tôi. Cứ tưởng nước mắt tôi bị che đi do cơn mưa.)</li><li>Tớ… Tớ không biết.</li><li>(Ngắm nhìn cô gái. Tôi chưa bao giờ thấy cô ấy quanh đây trước kia. Trang phục trông như là đến từ một thế giới khác.)</li><li>Cơn mưa. Mưa to quá.</li><li>(Với lời nhắc nhở của cô, cơn mưa mà tôi không để ý bỗng chốc ầm bên tai. Cái lạnh của nước mưa thấm vào quần áo đột nhiên khiến tôi không chịu được.)</li><li>Đúng thế, trời đang mưa to. Hãy đi tìm nơi trú mưa trước đã.</li><li>Tại sao chúng ta cần phải trú mưa ?</li><li>Cứ đi vào trong trước đi. Không thể để bất kì ai thấy chúng ta trong bộ dạng này được.</li><li>Được thôi.</li><li>(Cô gái ấy theo tôi vào trong chỗ cầu thang. Cánh cửa sắt nặng nề đóng chặn cơn mưa bên ngoài, làm cho mọi thứ vừa xảy ra cứ như là giấc mơ. Nhưng bộ quần áo ướt đẫm nhắc nhở rằng vừa rồi là thật.)</li><li>Mọi người đều sợ hãi vì họ sẽ bị bất tỉnh nếu bị dính nước mưa ?</li><li>Đúng vậy, và cậu trông có vẻ không bị sao cả. Mọi người có thể coi cậu như là người ngoài hành tinh hoặc… sự nguy hiểm.</li><li>Cậu cũng có thể đứng dưới mưa kìa.</li><li>Tớ… Tớ cũng không biết tại sao…</li><li>(Tôi chỉ nhớ về giấc mơ, và khi tỉnh lại, tôi đã đứng sẵn dưới mưa.)</li><li>Cậu có… nơi nào để ở không ?</li><li>Không.</li><li>Đương nhiên rồi… Cậu có tên không ?</li><li>Không.</li><li>Được rồi… Trước tiên tìm cho cậu một nơi để ở đã. Rồi cậu sẽ có tên một ngày nào đó thôi.</li><li>Cậu có thể ở đây từ bây giờ. Tớ sẽ kiểm tra cậu thường xuyên. Và nhớ rằng: <i>Đừng</i> có nói chuyện với bất kì ai.</li><li>(Căn phòng kho nhỏ trong chỗ cầu thang, bị bỏ hoang đã lâu, một nơi hoàn hảo để giấu ai đó. Hầu như không có ai đến đây, chứ đừng nói đến việc kiểm tra bên trong.)</li><li>Được thôi.</li><li>(Cô gái bí ẩn cứ như là đến từ thế giới khác qua vẻ ngây thơ ấy. Liệu cô có thật sự thích nghi được với cuộc sống nơi đây… ?)</li><li>(Có lẽ bây giờ… Tôi nên thực hiện từng bước một để xem mọi chuyện sẽ diễn ra như thế nào)</li><li>…</li><li>(Mặc dù tôi có rất nhiều câu hỏi, chúng tôi cần phải vào trong nhà. Cảnh tượng này không thể để cho người khác nhìn thấy được.)</li><li>(Tôi nhanh chóng giải thích vì sao mọi người cần phải tránh khỏi cơn mưa. Trong khi đang trò chuyện, tôi nhận ra quần áo của cô không hề bị ướt tí nào.)</li><li>(Hơi ấm từ cơ thể tôi làm cho quần áo sắp khô rồi. Mặc dù tôi muốn quay trở lại với mọi người, chỉ là không thể cứ để mặc cô gái một mình được.)</li></ul><h4 id="_1-2" tabindex="-1">1-2 <a class="header-anchor" href="#_1-2" aria-label="Permalink to &quot;1-2&quot;">​</a></h4>',13)),i("ul",null,[n[2]||(n[2]=h("<li>Ưm, Tớ mang cho cậu một ít nấm mới nướng. Được rắc lên một chút hạt tiêu và có lẽ mùi vị sẽ ổn thôi. Còn đây là một quyển truyện.</li><li>Bạn đây rồi.</li><li>(Tôi đứng trước cánh cửa căn phòng nhỏ và gõ cửa theo nhịp điệu cụ thể. Đây là dấu hiệu mà chúng tôi đã thống nhất - nếu không phải tiếng gõ này, cô phải trốn trong đấy.)</li><li>(Thời gian trôi qua rất nhanh kể từ lúc đó. Mỗi ngày tôi mang cho cô ấy đồ ăn và một vài cuốn sách, và cô gái có vẻ đang dần thích nghi được với cuộc sống này.)</li><li>Nấm. Lại là nó.</li><li>Tớ biết, nhưng quanh đây chẳng có mấy đồ ăn nào khác cả. Những món ăn ngon chỉ có ở lễ hội và lễ kỉ niệm. Chỉ có ăn món này hoặc là chết đói.</li><li>&#39;Lễ hội&#39; ?</li><li>Đó là lúc mà có chuyện tốt đẹp xảy ra, và mọi người sẽ quây quần lại để ăn những món ăn ngon.</li><li>Nếu có thể, ước gì tớ mang được cho cậu món gì đó khác…</li><li>Nấm nướng cũng ngon mà.</li><li>Thật vậy sao ? Mọi người quanh đây còn đang phát ngán với chúng nè…</li><li>(Tôi đặt những chiếc nấm được gói lại lên bàn và để ý thấy những quyển sách tôi mang cho cô ấy trước đó vẫn còn nguyên đấy.)</li><li>Cậu… không thể hiểu được những gì trong đó sao ?</li><li>Không.</li><li>À rồi… đây, cậu cầm lấy cái này đi.</li><li>(Cô ấy cầm lấy ảnh tôi đưa cho. Nó đã bị ố vàng và mờ đi, nhưng khung cảm trong đó vẫn rất đẹp đẽ.)</li><li>…</li><li>(Đôi mắt của cô dường như mở to hơn chút, như thể đã bị thu hút bởi cảnh tượng đó.)</li><li>Đẹp quá. Đây là gì thế ?</li><li>Đó là những bức ảnh về các địa điểm trên khắp thế giới. Nhưng chúng… không còn tồn tại nữa. Chỉ còn lại những tấm hình này.</li><li>Nếu cậu thấy thích, tớ có thể cho cậu xem thêm nhiều bức ảnh hơn về các nơi khác nhé ?</li><li>Được… !</li><li>(Kể từ đó, Tôi thường mang cho cô ấy nhiều bức ảnh và album. Cô rất thích ngắm nhìn chúng. Suy cho cùng, ai lại không bị mê hoặc bởi những thứ đẹp đẽ, mà đã mất trong quá khứ ?)</li><li>(Hôm nay, Tôi dự định mang cho cô gái nhiều ảnh và thức ăn, như thường lệ.)</li><li>(Ngay khi tôi đến chỗ cầu thang, thấy cô đang đứng đó, trông có vẻ lạc lõng.)</li><li>Ơ kìa, Cậu… Cậu đang làm gì thế !?</li><li>Ra ngoài. Tớ muốn ra ngoài, để ngắm nhìn thế giới trong những bức ảnh đấy. Tớ không muốn ở lại căn phòng tồi tàn này nữa.</li><li>……</li><li>(Phải rồi. Chẳng có ai muốn bị nhốt lại mãi trong không gian bé nhỏ. Cô ấy muốn ra ngoài. Và sẽ ổn thôi nếu không có ai đó tìm thấy cô ở đây…)</li><li>Đợi ở đây đi. Tớ sẽ lấy cho cậu một cái áo khoác để che lại. Cậu sẽ bị phát hiện nếu đi loanh quanh như thế này.</li><li>Cậu sẽ đưa tớ ra ngoài chứ ?</li><li>Tớ hứa.</li><li>Cảm ơn.</li><li>(Tôi phải &quot;mượn&quot; một chiếc áo choàng của thợ sửa chữa, lớp vải dày tối màu đủ để che đi gương mặt và cơ thể của cô.)</li><li>Nhớ nhé, cậu phải luôn đội mũ trùm đầu lại. Hứa với tớ đi.</li><li>Ừm.</li><li>Chúng tôi rời khỏi chỗ cầu thang, vượt qua những hàng rào dựng tạm bợ bằng bao cát và những đồ đạc bỏ đi, rồi bước vào khu vực nơi tôi và những người khác sinh sống.)</li><li>(Các căn phòng trong tòa nhà chứa đầy những chiếc lều vá, những chiếc giường làm tạm những bằng ván gỗ và những thanh kim loại, và một số người chỉ ngủ trên những tấm bìa dày cứng.)</li><li>(Những chiếc đèn an toàn và mùi ga đèn rải rác quanh đây. Không khí có mùi của lưu huỳnh.)</li><li>Này, cơn bão hôm qua lại làm hỏng tấm chắn mưa nữa hả? Nền nhà đang bị ướt kìa.</li><li>Làm sao mà có thể được? Tôi chỉ vừa mới thay tấm mới tuần trước mà.</li><li>Ông có cắt góc nào hoặc đang lừa chúng tôi không? Nghe này, nếu tòa nhà này mà sụp đổ, ông sẽ <i>không</i> dễ dàng để tránh khỏi nó đâu !</li><li>Gần đây nấm thu hoạch được rất ít… nếu cứ tiếp tục như thế này, đàn lợn cũng chẳng có gì để cho ăn…</li><li>Những con gà vẫn đẻ trứng, nhưng không có đủ dinh dưỡng. Rất nhiều quả có vỏ mềm.</li><li>Gần đây tôi nghe thấy có tiếng động lạ phát ra từ phòng điện thường xuyên… có lẽ bị ma ám rồi ?</li><li>Đúng hơn là có ai đấy đang trộm nguồn điện của chúng ta.</li><li>(Nơi đây thật hỗn loạn và đông đúc với những tiếng ồn. Mọi người chật vật để sinh tồn. Dù vậy, tất cả những gì chúng ta có thể làm là cố gắng sống sót thêm vài ngày nữa trong môi trường này.)</li><li>…</li><li>Tôi cảm thấy tay mình bỗng bị nắm chặt và nhận ra cô ấy đang nắm tay tôi.)</li><li>Sao vậy ?</li>",50)),i("li",null,[c(b,{intensity:"3"},{default:m(()=>[n[0]||(n[0]=g("Ở đây… ")),c(r,{duration:"1"}),n[1]||(n[1]=g("không giống như trong bức ảnh…"))]),_:1})]),n[3]||(n[3]=h("<li>… Nhanh nào, để tớ cho cậu xem thứ này.</li><li>(Tôi dẫn cô gái ra khỏi đám đông, đi xuống một tầng, và vào trong một căn phòng yên tĩnh. Ở đây, tiếng mưa gần như không có, và chỉ còn tiếng bước chân vang lên của chúng tôi.)</li><li>Đây rồi.</li><li>(Chúng tôi bước vào trong căn phòng với một chiếc giường. Có ai đang nằm trên đó, bao quanh bởi nhiều chiếc máy móc khác nhau kết nối với họ thông qua các điện cực và ống dẫn.)</li><li>(Là Susan.)</li><li>Có chuyện gì đã xảy ra với cô ấy ?</li><li>(Tôi lấy ra một mảnh giấy nhàu nát và đưa cho cô ấy.)</li><li>(Đó là bức ảnh một người phụ nữ trên giường đang ôm một đứa trẻ mới chào đời.)</li><li>Tên cô ấy là Susan. Gần đây cô ấy có một đứa con. Lẽ ra đó có thể là một ra đình hạnh phúc, nhưng chồng cô bỏ đi, và đứa con của cô đã bị bắt đi.</li><li>Bây giờ, cô ấy thành ra như thế này. Đây là những gì xảy ra nếu con người bị dính mưa. Họ không bao giờ tỉnh lại và phải dựa vào những cỗ máy này để duy trì sự sống.</li><li>Đó là lý do tại sao những cảnh đẹp đều biến mất. Cơn mưa đã cuốn trôi chúng đi… chúng ta sống dưới mái nhà, như bị giam cầm.</li><li>Giấc mơ của cô…… <i>đẹp quá</i>…</li>",12))]),n[5]||(n[5]=h('<p>**choice: **<br><br></p><ul><li>Đó là những gì chúng ta có</li><li>Ước gì tớ có thể mang được món gì khác</li><li>Theo tớ</li></ul><h4 id="_1-3" tabindex="-1">1-3 <a class="header-anchor" href="#_1-3" aria-label="Permalink to &quot;1-3&quot;">​</a></h4><ul><li>(Kế từ lần cuối chúng tôi đi ra ngoài, cô ấy thường đi lang thang một mình.)</li><li>Tớ nghe được người dân nói chuyện ở chỗ cầu thang. Họ bàn nhau việc tắt máy giúp duy trì sự sống của Susan.</li><li>Họ bảo không hề đáng để lãng phí tài nguyên vào người nào đó mà mãi không chịu dậy.</li><li>Hả ? Họ sao mà có thể làm được việc đó !?</li><li>Dù vậy, nhưng họ không thể… như thế là <i>giết người</i></li><li>(Khi tôi đang trầm ngâm, một bóng người nhỏ bé đội mũ trùm đầu xuất hiện trước mặt tôi.)</li><li>Cậu đang làm gì ở đây thế ?</li><li>Ý cậu là sao ?</li><li>Susan… đang gặp nguy hiểm.</li><li>Mình không hiểu tại sao, nhưng mình muốn cứu Susan.</li><li>Tớ biết.</li><li>Vì cậu là một người tốt.</li><li>Hả… Chỉ có vậy thôi sao? Nhưng nói thì dễ hơn làm. Chúng ta không thể cứ <i>làm</i> cho ai đó nói là họ muốn dậy được.</li><li>Cậu… có thể làm được sao ?</li><li>Nếu Susan mà tỉnh lại, cô ấy sẽ an toàn, đúng chứ ?</li><li>Chúng ta sẽ vào trong giấc mơ của cô ấy.</li><li>… Hả?! Nhưng… làm thế nào?</li><li>Đúng thế.</li><li>Nhưng… làm sao chúng ta cứu cô ấy được? Không thể cứ thuyết phục họ là không được…</li><li>(Thật nhẽ nhõm vì tôi đã bảo cô ấy không được nói chuyện với ai, nhưng tôi vẫn lo lắng về khả năng cô ấy bị lộ.)</li><li>Nhưng… cô ấy nhìn được giấc mơ của người khác… những bí ẩn về cô cứ chồng chất lên.</li></ul><p>**choice: **<br><br></p><ul><li>Cậu đúng là một người tốt</li><li>Họ không có quyền gì cả</li></ul><h4 id="_1-4" tabindex="-1">1-4 <a class="header-anchor" href="#_1-4" aria-label="Permalink to &quot;1-4&quot;">​</a></h4><ul><li>(Máy duy trì sự sống vẫn chạy đều đặn.)</li><li>(Susan nằm yên bình trên giường bệnh; chúng ta không thể để họ quyết định sự sống của cô ấy.)</li><li>Ánh mắt cậu nhìn trông thật đáng sợ.</li><li>… Thật sao ?</li><li>Ưm, như những người đánh nhau trong bức ảnh tớ thấy.</li><li>Xin lỗi… vừa rồi tớ chỉ đang nghĩ ngợi, chúng ta phải đánh thức Susan.</li><li>Ừm.</li><li>Vậy, để tớ kiểm tra một lần nữa… Chỉ cần nắm tay cậu, và rồi chúng ta sẽ vào giấc mơ của cô ấy ?</li><li>Đúng vậy, và cậu chỉ cần để tớ lo phần còn lại.</li><li>Đúng là một năng lực tiện ghê…</li><li>Nhưng… Tớ không biết sẽ xảy ra chuyện gì trong thế giới của những giấc mơ.</li><li>Nếu chúng ta bị thương ở đó, cũng có thể ảnh hưởng đến cả cơ thể ở thực tại.</li><li>Cậu sẵn sàng chưa ?</li><li>Sẽ ổn thôi. Nếu tớ không tỉnh dậy trong cơn mưa vì lý do kì lạ nào đó, tớ sẽ giống như Susan, thậm chí thiết bị duy trì sự sống của tớ sẽ bị tắt.</li><li>Vì tớ đã được cho cơ hội thứ hai trong đời, nên tớ phải làm cho nó trở nên thật ý nghĩa.</li><li>(Cô ấy nhìn vào mắt tôi, đôi mắt trong veo ấy dường như nhìn xuyên thấu tôi.)</li><li>Cậu có vẻ đã sẵn sàng.</li><li>Nắm lấy tay tớ.</li><li>Rồi.</li><li>(Tôi nắm chặt tay cô gái; nó lạnh như mưa.)</li><li>(Cô ấy nhìn thẳng vào Susan, đưa tay còn lại chạm vào cơ thể Susan.)</li><li>(Thật không thể tin nổi… bước vào giấc mơ của ai đó và các thư các thứ.)</li></ul><p>**choice: **<br><br></p><ul><li>Được thôi</li></ul><h4 id="_1-5" tabindex="-1">1-5 <a class="header-anchor" href="#_1-5" aria-label="Permalink to &quot;1-5&quot;">​</a></h4><ul><li>Ư…</li><li>(Mắt tôi bỗng tối sầm lại, như thể có thứ gì đó vô tình nhắm mắt tôi lại.)</li><li>(Mùi thơm ngọt ngào xộc thẳng vào mũi tôi, hấp dẫn hơn cả nấm nướng và nước luộc thịt.)</li><li>Đây là đâu đây… ?</li><li>(Tôi từ từ mở mắt và ngay lập tức bị ấn tượng bởi cảnh tượng trông thấy.)</li><li>(Đó là một căn phòng gọn gàng, với nội thất được sắp xếp như phòng khách. Không gian sáng hơn cả bất kì chiếc đèn khẩn cấp nào…)</li><li>(Ánh nắng chiếu qua cửa sổ.)</li><li>(Tôi không thể nhìn được có gì ở bên ngoài)</li><li>(Không có tiếng mưa…)</li><li>(Liệu tôi… có đang ở thế giới của những <i>giấc mơ ?</i>)</li><li>Và đó là giấc mơ của Susan.</li><li>Nó gần giống như những gì tớ được thấy qua những bức ảnh. Thật là đẹp quá…</li><li>Đúng… Nó hoàn toàn khác xa với thế giới thực mà chúng ta đang sống.</li><li>Ồ, chúng ta có khách sao ?</li><li>(Susan bước ra khỏi phòng, quần áo sạch sẽ. Trên khuôn mặt cô ấy tỏ ra rất thoải mái và hài lòng.)</li><li>(Nhưng thực tế, cô đáng ra là đang nằm trên giường, kết nối với máy duy trì sự sống…)</li><li>(Ra đây là cách cô ấy nhìn chính mình trong giấc mơ ư ?)</li><li>Su… <i>Dì Susan.</i></li><li>À, thì ra là cháu. Người mà hay đứng trong góc phòng.</li><li>Hả… Góc nào…</li><li>Hmm… dì cũng không nhớ nữa. Vậy, còn cô gái trẻ bên cạnh cháu là ai ?</li><li>Ùm… Cô ấy… là bạn của cháu.</li><li>Ồ, vậy tên cháu là gì, cô bé ?</li><li>Cháu không có tên.</li><li>Không có tên sao? Được rồi… Dì cứ gọi cháu là cô bé nhé.</li><li>Được ạ.</li><li>Robert, chúng ta có khách đến chơi nhà này! Ra đây và gặp họ đi nào!</li><li>Ồ, xin chào. Xin lỗi vì chú không ra sớm để chào các cháu.</li><li>(Anh ấy đáng nhẽ ra phải ở trên mặt đất…)</li><li>(Đi làm việc ở một số công trình xây dựng…)</li><li>Chào…</li><li>À phải rồi, bánh quy sắp xong rồi đó. Tại sao cháu không ngồi xuống đây và dì sẽ đi kiểm tra chúng.</li><li>(Susan quay người và đi vào phòng bếp, nơi mà thế giới thực cô ấy chỉ có thể nấu ăn bằng ngọn lửa và một cái nồi, nhưng ở đây, cô có nguyên cái lò nướng.)</li><li>Cô ấy <i>yêu thích</i> nướng bánh quy lắm, và chúng ngon tuyệt vời! Cháu nên thử một ít nếu cháu thích.</li><li>Chúng cháu… chúng cháu cũng thích ạ.</li><li>Thoải mái đi, hãy cứ tự nhiên như ở nhà, trong khi chú phải quay lại làm thiết kế; bận cực kì.</li><li>Bọn cháu hiểu rồi.</li><li>(Sau khi Robert rời đi, Tôi đến gần cánh cửa, muốn xem khung cảnh bên ngoài ngôi nhà.)</li><li>(Nhưng khi tôi vặn tay nắm cửa, tôi nhận ra không thể mở được, như thể cánh cửa chỉ là một bức tường.)</li><li>Không mở được…</li><li>Ngôi nhà là giấc mơ của Susan rồi.</li><li>Nên sẽ không có thế giới nào bên ngoài cánh cửa…</li><li>Bánh quy nướng xong rồi! Lại đây và thử xem!</li><li>(Susan đeo đôi găng tay dành cho lò nướng, và trên khay nướng là những chiếc bánh quy mới được sắp xếp gọn gàng, bề mặt được nạm vàng.)</li><li>Vâng, cháu cảm ơn ạ.</li><li>(Tôi lấy một cái và đưa một cái khác cho cô gái, rồi cắn một miếng.)</li><li>Nó… ngon tuyệt vời !</li><li>Thật ngọt ngào…</li><li>(Chưa bao giờ tôi được nếm thứ gì thơm ngọt, giòn tan đến thế. Vị đắng nhẹ của socola hòa quyền với bị ngọt của bánh quy, sưởi ấm cả thể xác lẫn tâm hồn.)</li><li>Hehehe, cứ lấy thêm nếu cháu muốn. Nhìn người khác tận hưởng đồ ăn nhẹ cô làm đã là phần thưởng lớn nhất với cô rồi.</li><li>(Trước khi tôi kịp hành động, cô gái bên cạnh tôi đã háo hức cầm lên một cái khác ăn, cứ như là trẻ con vậy.)</li><li>(Đến cuối cùng, chúng tôi đã qua đêm trong phòng khách theo lời mời nồng nhiệt của Robert và Susan.)</li><li>(Và ngoài ra, không còn nơi nào khác để đi.)</li><li>Trông giống như khung cảnh ở thế giới thực vào buổi đêm.</li><li>Và không có mưa…</li><li>Đúng là một nơi thật hạnh phúc…</li><li>Nhưng… chúng ta cần phải đánh thức cô ấy dậy. Đó chỉ là giấc mơ. Không phải thực tế…</li><li>Thế ngày mai chúng ta sẽ thuyết phục cô ấy tỉnh dậy.</li><li>Phải rồi, nếu không thì cô ấy sẽ…</li><li>(Một người đàn ông khác bước ra từ căn phòng, tất nhiên là chồng của Susan rồi.)</li></ul><h4 id="_1-6" tabindex="-1">1-6 <a class="header-anchor" href="#_1-6" aria-label="Permalink to &quot;1-6&quot;">​</a></h4><ul><li>(Susan đang làm bữa trưa. Trông giống như là nồi súp.)</li><li>(Mùi hương có gì đó rất quen thuộc, giống như mùi nấm và thịt với gia vị.)</li><li>(Tôi gọi Susan ra phòng khách.)</li><li>Dì Susan ơi, cháu có chuyện cần nói.</li><li>Gì thế cháu? Cô bé đang đói hả? Bữa trưa sẽ xong ngay trong 1 phút.</li><li>Không—không, không phải là như vậy… Ừm để—để cháu nói thẳng luôn với cô. Nghe cháu, thế giới cô đang ở bây giờ, chúng chỉ là <i>giấc mơ</i>, không phải thực tế.</li><li>Cháu đang nói gì vậy ? Cái gì giấc mơ, cái gì thực tế cơ?</li><li>Trong thế giới thực, dì đang nằm trên giường bệnh viện, có thể nguy hiểm đến tính mạng bất cứ lúc nào.</li><li>Và dì chỉ cần thức dậy…</li><li>KHÔNG.</li><li>Hả… ?</li><li>Dì thích cuộc sống ở đây. Dì không muốn mọi thứ bị thay đổi.</li><li>Giả sử tất cả những điều vừa rồi là giấc mơ, thì sao nào ?</li><li>Ở đây thật hạnh phúc, đúng chứ !</li><li>Dì có thể ăn bánh quy mới nướng, uống nước súp nóng, ngon.</li><li>Và có một người chồng yêu thương ở bên.</li><li>Nếu thực tế tuyệt vời như vậy, thế sao dì lại ở đây từ đầu !?</li><li>Nhưng…</li><li>Dì không muốn rời đi, kể cả đây chỉ là giấc mơ !</li><li>Và nếu dì qua đời, thà đi từ hạnh phúc ngắn ngủi đến cái chết còn hơn là sống trong đau khổ lâu dài đến cuối đời, đúng không ?</li><li>…</li><li>(Yên lặng.)</li><li>Dì xin lỗi… nồi súp… sắp sôi rồi, dì phải đi kiểm tra đã.</li><li>(Cô đi vào phòng bếp.)</li><li>Tớ cảm thấy như cô ấy đang buồn.</li><li>Có lẽ do tớ vừa rồi thẳng thắn quá…</li><li>Là vậy sao ?</li><li>Thật tàn nhẫn khi để họ thức dậy sau giấc mơ hạnh phúc…</li><li>Tuy nhiên… giấc mơ có thể rất đẹp, nhưng đây chỉ là ảo ảnh thoáng qua nhằm làm tê liệt chính mình.</li><li>(Cô gái đưa cho tôi tờ giấy bị nhàu nát với hình vẽ nguệch ngoạc về Susan và con của dì.)</li><li>Dì Susan trong bức vẽ trông rất vui vẻ.</li><li>…</li><li>Tớ mượn được không? Tớ đi nói lại với dì ấy lần nữa.</li><li>(Tôi cầm lấy bức ảnh và đi vào phòng bếp. Susan đang đứng trước lò bếp, trầm ngâm suy nghĩ.)</li><li>Dì Susan… dì có nhớ cái này không ?</li><li>…hả ?</li><li>(Khuôn mặt dì trông mệt mỏi, đôi mắt có hơi đỏ.)</li><li>(Tôi đưa cho dì ấy bức vẽ, dì cầm lấy nó, và nhìn vào hình vẽ nguệch ngoạc một thời gian dài, trầm ngâm.)</li><li>…</li><li>…</li><li>Oaa…a…oaaa…</li><li>(Nước mắt chảy xuống trên khuôn mặt, tiếng khóc đầy đau buồn xuyên qua trái tim tôi.)</li><li>（……）</li><li>Giấc mơ… a…</li><li>Tôi đúng là một người mẹ vô trách nhiệm… Tôi thực sự đã <i>bỏ rơi</i> con mình…</li><li>Dì Susan…</li><li>Không có gì đáng xấu hổ khi trốn tránh đối mặt với áp lực.</li><li>Kể cả không xấu hổ, nhưng vẫn là sai.</li><li>Kể từ khi dì nhìn thấy hai cháu hôm qua, dì đã tự hỏi suốt liệu đã từng gặp cháu trước đây và ở đâu.</li><li>Rồi chợt nhận ra, dì bắt đầu nhớ lại nhiều thứ, nhiều thứ ở thế giới thực…</li><li>Dì nghĩ dì ghét đứa con của dì. Dì nghĩ… nếu dì chưa từng bao giờ có anh ấy, thì những chuyện này sẽ không bao giờ xảy ra…</li><li>Dì đã từng rất sợ, về thế giới mưa đó. Dì không… dì không muốn rời khỏi nơi đây. Mọi thứ thật hoàn hảo…</li><li>Nhưng chỉ là giấc mơ…</li><li>(Susan nhẹ nhàng vuốt ve đứa bé trong tranh, một nụ cười giống như trong bức vẻ hiện lên trên gương mặt cô.)</li><li>Dì sẽ quay trở lại.</li><li>Dì biết là bây giờ. Dì… Dì không hề ghét chú ấy, dì chỉ… đổ lỗi cho chú, vì tất cả mọi thứ.</li><li>Coi anh ta như là nguyên nhân của mọi nỗi đau của dì…</li><li>Dì chỉ không đủ can đảm để chấp nhận mọi chuyện đã xảy ra…</li><li>Cháu xin lấy làm tiếc…</li><li>Bây giờ dì muốn quay trở lại.</li><li>Dì muốn chờ đợi đến khi đứa bé lớn lên. Có khi một ngày nào đứa bé ấy sẽ quay lại với dì. Và khi chuyện này xảy ra, dì sẽ chào mừng đứa bé quay trở lại.</li><li>Mặc dù không có bánh quy mới nướng, nhưng dì vẫn sẽ làm món gì đó ngon cho đứa trẻ.</li><li>Chắc chắn dì sẽ làm được mà.</li><li>Liệu dì… có thể nói chào tạm biệt chồng dì được không? Dì biết anh ấy không phải là thật nhưng…</li><li>Dì thật là một người dịu dàng, dì biết mà, đúng không ?</li><li>(Tôi chờ đợi với cô gái ở phòng khách để Susan quay lại sau khi chào tạm biệt.)</li><li>Cô ấy trông thật là hạnh phúc. Thậm chí quyết tâm. Tớ có thể nhìn thấy qua đôi mắt đó.</li><li>Nhưng cậu nên biết là, sẽ không có tí bánh quy nào khi chúng ta tỉnh lại.</li><li>Cô ấy quan trọng hơn.</li><li>Phải rồi.</li><li>Dì sẵn sàng rồi.</li><li>Cảm ơn cháu.</li><li>Không có gì cần phải cảm ơn bọn cháu đâu, thật sự đấy.</li><li>Bọn cháu chỉ muốn giúp thôi.</li><li>Đừng nói thế. Cháu đã cho dì lòng can đảm.</li><li>(Dì ấy dừng lại và bước ra khỏi cửa.)</li><li>(Nhẹ nhàng xoay tay cầm, cô đẩy cửa mở ra, để một ánh sáng trắng dịu nhẹ lọt vào. Đầu tiên bao bọc Susan, rồi tôi và cô gái ấy, tỏa ra hơi ấm xung quanh cho đến cuối cùng bao chùm cả 3 chúng tôi.)</li></ul><p>**choice: **<br><br></p><ul><li><em>Yên lặng</em></li><li>Không có gì đáng xấu hổ khi trốn tránh</li></ul><h4 id="_1-7" tabindex="-1">1-7 <a class="header-anchor" href="#_1-7" aria-label="Permalink to &quot;1-7&quot;">​</a></h4><ul><li>(Tiếng mưa như trút nước lại vang lên.)</li><li>(Càng lúc càng thấy khó chịu thật.)</li><li>(Tiếng bíp nhịp nhàng từ máy hỗ trợ sự sống…)</li><li>Chúng ta… quay lại rồi sao… ?</li><li>Chào buổi sáng, các cháu.</li><li>Sáng vui vẻ ạ.</li><li>Nghe tiếng mưa khó chịu này thực sự bây giờ làm dì cảm thấy dễ chịu hơn chút.</li><li>Mừng quay trở lại, dì Susan.</li><li>… Dì đã bị bất tỉnh bao lâu rồi ?</li><li>Khoảng một tháng rồi hoặc lâu hơn.</li><li>Đúng lâu thật…</li><li>(Khuôn mặt cô không còn vẻ khỏe mạnh như trong mơ. Phụ thuộc vào máy quá lâu đã khiến cô trở nên yếu đuối, nhưng đôi mắt cô vẫn tỏ ra mạnh mẽ.)</li><li>Giúp dì dậy với. Dì cần quay lại và kiểm tra lều của dì.</li><li>Có cháu đây. Dì cứ bình tĩnh.</li><li>Su-Susan! ? Cô… cô đã tỉnh lại ! ?</li><li>(Trước khi kịp trả lời, anh ấy đã chạy đi, và chẳng bao lâu mọi người đều biết Susan đã thức dậy.)</li><li>(Người dân quây quanh chúng tôi rất nhanh.)</li><li>Susan thật sự đã tỉnh lại !</li><li>Liệu đây có phải là một phép màu ?</li><li>Có lẽ ảnh hưởng của cơn mưa không kéo dài mãi mãi… ?</li><li>Ơn trời… Susan cuối cùng cũng tỉnh dậy…</li><li>Có phải hai cháu đánh thức Susan dậy ?</li><li>Cháu làm thế nào vậy! ?</li><li>Chúng ta cần phải chúc mừng Susan đã quay trở lại.</li><li>Đúng đúng, hãy cùng ăn mừng nào !</li><li>Có vẻ mọi thứ đã diễn ra tốt đẹp.</li><li>… Phải.</li><li>Ngươi… hai người… chắc chắn là ác quỷ ! !</li><li>(Một giọng nói xé ngang đám đông, khiến mọi người im lặng.)</li><li>(Mọi ánh mắt đổ dồn về hướng phát ra tiếng nói đó.)</li><li>Ác quỷ! Ngươi-ngươi sẽ không thể cứ thể gọi mọi người dậy! Ngươi chắc hẳn phải lập ra khế ước với ác quỷ để gọi cô ấy dậy !</li><li>Hả gì… ?</li><li>Họ không phải ác quỷ hoặc người xấu !</li><li>Thế cô sẽ chứng minh như thế nào ?</li><li>Nhìn vào cô ta đi! Không ai trong chúng ta từng nhìn thấy ả ta trước đây! Chắc chắn là được các quỷ dữ gửi lên!</li><li>(Tôi đứng ra bảo vệ cô gái.)</li><li>Cô ấy không phải !</li><li>Thế nói cho chúng tôi xem cô ta là ai !</li><li>… <i>Chậc…</i> Cháu không biết.</li><li>(Tôi không thể nói về cô gái đó và cơn mưa, hoặc cách tôi tỉnh lại trong mưa.)</li><li>Ngươi không biết? Rất là đáng nghi, đúng chứ !?</li><li>Susan, cô đã lập thỏa thuận gì với chúng? Kể chúng tôi nghe!</li><li>Chúng tôi không hề có thỏa thuận gì. Họ chỉ… cứu tôi.</li><li>Đéo tin! Làm sao mà bất kì ai có thể tỉnh lại sau ảnh hưởng do mưa như vậy được ?</li><li>Quá chuẩn… không có ai khác đã từng tỉnh lại trước kia…</li><li>Thế sao ngươi không cứu bố tôi? Chắc chắn là do khế ước với quỷ dữ!</li><li>Các ngươi giải thích bản thân đi!</li><li>Đó có phải do trả giá bằng mạng sống của người khác để cứu họ ?</li><li>(Người dân bắt đầu tin vào những lời buộc tội…)</li><li>(Nếu tôi nói cho họ biết sự thật, họ chắc chắn sẽ tin rằng cô ấy là một con quỷ thực sự. Tệ thật, tôi nên làm gì bây giờ…?)</li><li>Trói chặt chúng lại !</li><li>Đúng… Phải rồi, trói hết lại !</li><li>Tránh xa khỏi chúng tôi đi, lũ quái đản !</li><li>(…)</li><li>(Chúng tôi bị tách ra vào trong các căn phòng khác nhau, mỗi phòng đều có canh gác, không thể nhìn thấy nhau.)</li><li>Tôi không biết là ở đây lại có nhiều phòng trống đến vậy. Phòng cách ly độc lập, một sự xa xỉ mà trước đây chúng tôi không có được.</li><li>(Susan và cô gái ấy đang làm gì nhỉ? Liệu họ có ổn không?)</li><li>(Chết thiệt… chúng tôi chỉ vừa mới cứu Susan…)</li><li>(…Nhưng nếu chúng tôi chưa từng cứu dì ấy, chắc chuyện này sẽ không bao giờ xảy ra chứ nhỉ…?)</li><li>(<i>Đùng !</i>)</li><li>(Tiếng ầm phát ra từ bên ngoài cửa.)</li><li>(Cánh cửa mở ra, và bước vào là Susan và dì ấy.)</li><li>Làm… làm sao dì thoát được ra vậy… ?!</li><li>Họ tưởng họ có thể nhốt gì vào một căn phòng ngẫu nhiên nào đó. Không có cửa đâu.</li><li>(Tôi liếc nhìn người nằm trên sàn bên ngoài và cây gậy sắt trong tay Susan.)</li><li>Chỉ là dì cho hắn ngủ chút thôi mà.</li><li>Tuy là vẫn còn rất lo lắng, Nhưng đây là lần đầu tiên dì làm chuyện này…</li><li>Cô ấy rất giỏi. Cô đã đưa tớ ra ngoài.</li><li>Haha, thực ra thì, cháu đã gần như kiệt sức để đi…</li><li>Cháu chỉ vừa tỉnh lại. Này là quá khả năng với cháu rồi…</li><li>Không có thời gian để nghỉ đâu. Dì sẽ tóm tắt ngắn gọn.</li><li>Hai cháu đang ở trong tình huống rất nguy hiểm. Cháu cần phải chạy thoát. Ở đây chỉ có chết thôi.</li><li>… Chúng ta có thể đi đâu được ?</li><li>Lên trên mặt đất.</li><li>Các tầng gần mặt đất được đặt đầy những chiếc bẫy và trạm kiểm soát. Chúng ta không thể vượt qua nếu không có quyền.</li><li>Sử dụng những chỗ cầu thang bị bỏ hoang để lên trên mặt đất, sau đó đi qua bức tường bên ngoài. Chắc sẽ tìm được lối ra thôi.</li><li>Một hy vọng mong manh còn hơn là ở lại đây.</li><li>Dì không đi với chúng cháu sao ?</li><li>…Dì quá yếu để đi tiếp. Chỉ tổ làm chúng cháu chậm lại.</li><li>Có thể dì không thực hiện được lời hứa đã hứa lúc trước.</li><li>Nhưng dì muốn chồng của dì và đứa bé ấy biết họ đã có một người vợ và một người mẹ là một người phụ nữ đã từng dũng cảm thế nào.</li><li>Dì Susan…</li><li>Cầm lấy lá thư này. Nếu gặp chồng của dì, hãy đưa cho chú.</li><li>(Tôi kiên quyết nhận lấy lá thư.)</li><li>…cháu sẽ đưa. Cảm ơn dì.</li><li>Susan, cô đang khóc kìa.</li><li>Hehe… Còn gì đáng sợ hơn khi khóc đến mức không còn giọt nước mắt nào để khóc.</li><li>Cháu không có tên, đúng chứ? Cô bé?</li><li>Không.</li><li>Dì từng nghĩ nếu có một đứa con gái, dì sẽ đặt tên là Luvia.</li><li>Dì có thể gọi cháu như vậy không ?</li><li>… Được ạ.</li><li>Luvia… Đó là một cái tên rất hay.</li><li>Cảm ơn… cảm ơn cả hai cháu. Giờ thì đi ngay đi! Nếu họ quay lại, mọi thứ sẽ kết thúc. Để dì giữ chân họ lại! ĐI ĐI!</li><li>Đi thôi, <i>Luvia</i>.</li><li>(Tôi gật đầu với Susan. Nước cô mắt đẫm lệ, nhưng cô vẫn nở nụ cười. Có thể đây là lần cuối chúng tôi gặp nhau.)</li><li>Tạm biệt dì, Susan.</li><li>(Susan nhìn chúng tôi rời đi, dựa vào khung cửa, nước mắt rơi đẫm lệ.)</li><li>Dì xin lỗi… Dì có thể không bao giờ gặp cháu được nữa.</li><li>Dì ước chúng ta có thể là một gia đình trong thế giới không có mưa…</li></ul><p>**choice: **<br><br></p><ul><li>Đi thôi</li></ul><h4 id="尾声" tabindex="-1">尾声 <a class="header-anchor" href="#尾声" aria-label="Permalink to &quot;尾声&quot;">​</a></h4><ul><li>Vị máu trào lên trong cổ họng. Quần áo tôi ướt đẫm mồ hôi.</li><li>Tiếng bước chân cứ mãi vang vọng trong cầu thang.</li><li>Dù đôi chân tôi cảm thấy nặng trĩu đến mức không nhấc nổi, và tầm nhìn mờ đi vì thiếu không khí, tôi vẫn nắm chặt tay cô gái đi theo sau.</li><li>Hai mốt… hai mươi… mười chín…</li><li>Đầu óc tôi rối bời. Không thể tỉnh táo suy nghĩ được nữa.</li><li>Mười tám… mười bảy… mười sáu…</li><li>Con số giảm dần, từng bậc từng bậc một, nỗi bất an càng cuộn chặt lấy tôi.</li><li>Chúng tôi không biết điều gì đang chờ đợi phía trước.<br><br>Nhưng tôi biết, dù là gì đi nữa, đó chính là tự do.</li><li>Dù vậy tôi cũng chẳng chắc liệu &quot;tự do&quot; đó có phải là thứ chúng tôi cần hay không.</li><li>Vì sao chúng ta phải tiếp tục? Tại sao chúng ta cứ đếm ngược?<br><br>Bởi vì những gì đã đánh mất, mãi mãi chẳng thể lấy lại.</li><li>Tớ… Tớ không thể… đi tiếp được nữa…</li><li>Tôi quay lại và nhìn cô gái phía sau.<br><br>Gương mặt cô đỏ ửng, hơi thở gấp gáp, mồ hôi đọng trên trán.</li><li>Tôi chần chừ trong giây lát. Rồi không còn tiếng bước chân nào phía sau nữa.</li><li>Thật sự chúng ta cần nghỉ chân.</li><li>Chúng tôi ngồi xuống bậc thềm, chỉ một chút thôi.<br><br>Bên ngoài, mưa vẫn rơi.</li><li>Thật sự chúng ta… để dì Susan ở lại như vậy sao ?</li><li>…</li><li>Tại sao họ lại làm thế… ?</li><li>…</li><li>Sao cậu không trả lời tớ ?</li><li>Vì ngay cả bản thân tôi cũng chẳng tin vào câu trả lời mình sẽ đưa ra. Tôi không muốn tin rằng mọi người đã chấp nhận duy trì sống trong <b>tình cảnh hiện tại</b>.</li><li>Họ đã chấp nhận rằng tương lai sẽ không thể thay đổi. Bất cứ ai từ bỏ hi vọng sẽ bắt đầu coi như hi vọng là một lời nguyền.</li><li>Trong mắt họ… cố gắng làm mọi thứ tốt hơn cũng chẳng khác nào vô nghĩa.<br><br>Họ sẵn sàng làm mọi thứ để giữ nguyên <b>tình cảnh này</b> y như cũ.</li><li>Bởi vì những gì họ có thể thấy và chạm vào được <b>thì</b> họ mới tin. Còn tất cả những thứ khác, mọi thứ bất định... chỉ giống như mưa mà thôi.</li><li>Và chính vì như thế… họ… Tớ vẫn chưa hiểu…</li><li>Cô ấy lẩm bẩm một lúc lâu.<br><br>Sự im lặng của tôi không có nghĩa là phớt lờ sự thật. Chỉ là tôi không thể làm gì.</li><li>Trong lịch sử, con người đã làm nhiều điều to lớn để bảo vệ thế giới của họ.</li><li>Nếu họ không thể ngăn được cơn mưa, họ sẽ xóa bỏ tất cả những gì liên quan tới nó.</li><li>Cậu có nhớ cái tên mà dì Susan đã đặt cho cậu không ?</li><li>Cô ấy ngẩng đầu lên, ánh mắt lặng lẽ không cử động.</li><li>… <i>Luvia</i>.</li><li>Cậu có thích không ?</li><li>Tớ… Tớ có.</li><li>Tôi nhìn xuống bức thư vẫn bị vò trong tay mình. Bức thư được bọc một lớp nhựa, nhăn nhúm vì tôi đã nắm chặt quá lâu. Nhưng vẫn còn ở đây.</li><li>Luvia, tớ chắc chắn Susan muốn chúng ta sống. Dì ấy đã giao cho chúng ta một nhiệm vụ.</li><li>Tôi đứng dậy và đưa tay ra cho cô ấy.</li><li>Hãy kiên trì thêm chút nữa. Chúng ta sẽ cùng nhau nhìn thấy thế giới ngoài kia thực sự như thế nào.</li><li>Không vì lý do nào cả.</li><li>Chỉ để tới được nơi duy nhất không bị chi phối bởi <b>tình cảnh hiện tại</b>. Nơi duy nhất còn giữ lại sự bất định.</li><li>… Ưm.</li><li>Luvia nắm lấy tay tôi.</li><li>Mười… chín… tám</li><li>Tôi đã có thể nhìn thấy thành phố ngập nước bên ngoài cửa sổ cầu thang.<br><br>Những bức tưởng vỡ nát chìm dưới dòng nước. Cơn mưa đã cuốn trôi mọi thứ.</li><li>Sáu… năm…</li><li>Cuối cùng, một cánh cổng bị khóa chặn lại con đường phía trước.<br><br>Dải băng vàng-đen cảnh báo kéo căng trên đó. Biển báo ghi: CẤM LẠI GẦN.</li><li>Tôi đưa tay ra. Nhưng chỉ cần chạm vào, nó khiến ngón tay tôi giật lên như bị tĩnh điện.</li><li>Chết tiệt! Chúng ta đến <i>gần lắm rồi</i>.</li><li>Susan ắt hẳn phải biết chuyện này. Thế thì dì ấy muốn nói gì với chúng ta ?</li><li>Nơi này… đã bị niêm phong rồi sao ?</li><li>Ừm. Đúng vậy.</li><li>Dĩ nhiên là nó bị niêm phong rồi. Chúng ta phải tìm lối ra khác.</li><li>Tôi dẫn Luvia trở lại tầng trên.</li><li>Chúng tôi tìm kiếm trong cầu thang cho đến khi phát hiện ra một lối thoát mà trước đó không hề nhận ra — một lối thoát khác ra bên ngoài.</li><li>Cửa thoát hiểm ở ngay phía trước. Có lẽ vì nó dẫn thẳng ra mưa, nên vẫn chưa bị niêm phong.</li><li>Tôi dễ dàng đẩy cửa ra.<br><br>Cơn gió lạnh ào vào, mang theo một ánh sáng yếu ớt. Những giọt mưa đập vào mặt tôi như những cái kim châm.</li><li>Và rồi, chúng tôi thấy: thế giới như trước kia.</li><li>Một nơi mà chúng tôi chưa từng đặt chân đến, ngay trước mắt.</li><li>Xám xịt. Mọi thứ đều xám xịt. Thành phố từng vươn cao trên chúng tôi giờ đây cảm giác như quá gần, gần như không thật.</li><li>Mưa đã cuốn trôi mọi dấu vết của những gì đã từng tồn tại. Không còn chút sự sống nào.</li><li>Chẳng có gì ngoài sự im lặng. Tiếng mưa rơi xuống vang vọng như một bản giao hưởng bị đứt đoạn.</li><li>Đây là tất cả những gì thế giới cũ bỏ lại. Bị quên lãng và bị nhấn chìm.</li><li>Luvia bước vào vũng nước, bước chân cô tạo nên những đường gợn sóng trên mặt nước.</li><li>Cô ấy tiếp tục bước đi, cảm nhận sức nặng của nước mưa thấm đẫm người cô.</li><li>Có phải chúng ta… đến muộn rồi không ?</li><li>Đó là câu hỏi mà chẳng ai còn sống sẽ dám thốt ra. Đơn giản và buồn bã.</li><li>Cơn mưa hòa với cơn gió. Sương mù che khuất hình bóng cô gái, tựa như một giấc mơ.</li><li>Chân tôi tự động bước đi. Tôi muốn níu giữ một cái gì đó. Một cái gì đó quan trọng. Mặc dù tôi không biết đó là gì.</li><li>Vì… tôi vẫn chẳng hiểu gì về thế giới này.</li><li>Luvia.</li><li>Tôi lại nhẹ nhàng nắm lấy cổ tay cô ấy. Cô gái không lên tiếng. Cô ấy nhìn lên bầu trời, nơi chỉ có mưa.</li><li>Chưa muộn đâu. Vẫn còn rất nhiều thứ chúng ta chưa thấy.</li><li>Cho đến lúc đó… Tớ nghĩ chúng mình chỉ cần tiếp tục bước đi.</li><li>Sau tất cả, trong một thế giới chẳng còn lấy hi vọng, chúng ta không còn gì để mất nữa.</li><li>Chúng tôi không bước đi để cứu ai. Cũng không phải để thay đổi thế giới tàn khốc này.</li><li>Nhưng chúng tôi vẫn tiếp tục bước đi.</li><li>Và số phận vẫn còn đó, chờ đợi viết lại bằng chính đôi tay của <i>chúng ta</i>.</li></ul><hr><h2 id="ngoai-truyen" tabindex="-1">Ngoại Truyện <a id="side"></a> <a class="header-anchor" href="#ngoai-truyen" aria-label="Permalink to &quot;Ngoại Truyện &lt;a id=&quot;side&quot;&gt;&lt;/a&gt;&quot;">​</a></h2><h3 id="xuan-cho-trong-hoa" tabindex="-1">Xuân Chờ Trong Hoa <a id="side1"></a> <a class="header-anchor" href="#xuan-cho-trong-hoa" aria-label="Permalink to &quot;Xuân Chờ Trong Hoa &lt;a id=&quot;side1&quot;&gt;&lt;/a&gt;&quot;">​</a></h3><ul><li><a href="#contents">Contents</a></li></ul><p>Xưa kia núi non từng dâng tặng thần linh một thiếu nữ tuyệt sắc vô song.<br><br>Nhưng cũng như tảng đá cứng cỏi muôn đời phàm trần chẳng ưa chuộng,<br><br>cơn mưa vĩnh cửu rồi cũng xóa nhòa những đóa hoa trần thế.<br><br><br><br><br><br>Đã rất lâu, rất lâu rồi... nơi đây chẳng có lấy một giọt mưa.<br><br>Không ai còn nhớ từ bao giờ mọi thứ bắt đầu - chỉ biết rằng trời cứ mãi trong veo kể từ một ngày nào đó. Mặt trời gay gắt thiêu đốt từng thớ đất, sông ngòi thì cạn kiệt, đồng ruộng thì úa tàn. Giếng nước trong làng cũng gần như cạn đáy.<br><br>Người lớn trong làng vừa trở về từ chuyến đi tìm kiếm tài nguyên. Chỉ cần một ánh nhìn thoáng qua sắc mặt họ cũng đủ hiểu lại một lần nữa trở về trắng tay.<br><br>Bố mẹ cũng trong số đó. Làn da của họ còn đen sạm hơn trước dưới cái nắng chẳng có lấy chút khoan dung nào.<br><br>Tôi vội bỏ quả bóng thêu khỏi tay và lao nhanh về phía họ, líu ríu chạy đi.<br><br>&quot;Bố, Mẹ! Hôm nay con ở nhà như bố mẹ đã bảo ạ! Con không có chạy nhảy lung tung tùm lum tung tóe lòe xòe ra đâu, và con cũng không lãng phí một giọt nước nào cả! Con giỏi không ạ !&quot; Tôi mỉm cười toe toét, nhắm tít mắt lại, kiễng chân đưa đầu lại gần hai người. Chạy có chút xíu mà mệt thở muốn hết hơi.<br><br>&quot;Honoka ngoan lắm! Đúng là niềm tự hào của nhà Asagi mà! &quot; Bố mỉm cười xoa đầu tôi, tôi cũng cười khúc khích đáp lại.<br><br>&quot;Hứ! Chứ sao nữa! Cả cái nhà này là do con trông coi hết đó nha !&quot; Tôi tự hào tuyên bố.<br><br>Mẹ cúi xuống nhẹ nhàng đưa bàn tay chai sần áp nhẹ lên má tôi - cái chạm ấy thật khiến tôi thấy thật bình yên làm sao. &quot;Họ không có bắt nạt con khi bố mẹ đi vắng chứ? Con thấy có sao không ?&quot;.<br><br>&quot;Không có sao ạ !&quot; Tôi vẫn giữ nụ cười, chỉ khẽ gật đầu. Không thể nói cho Bố Mẹ biết tụi nhỏ đó lại đến chọc ghẹo, gọi tôi là đứa quái thai, bị nguyền rủa. Kể ra sẽ khiến họ buồn... hoặc tệ hơn là nổi giận.<br><br>&quot;Vậy là tốt rồi... Con đừng nghe những gì người ta bảo nhé. Chúng nó chỉ ghen tị với con thôi,&quot; Mẹ dịu dàng nói.<br><br>&quot;Ghen tị...? Ghen tị gì vậy ạ ?&quot; Tôi bối rối hỏi.<br><br>&quot;Ghen vì con là một đứa con ngoan, và có bố mẹ quan tâm chăm sóc cho con đến nhường nào.&quot; Người bố tiếp lời.<br><br>&quot;Ra là vậy! Nên tụi nó mới khinh thường gia đình mình - chúng nó chỉ đang ghen thôi !&quot;<br><br>&quot;Suỵt, nói nhỏ thôi con !&quot; Người mẹ vội bịt miệng tôi lại. &quot;Vào nhà nào, đứng ngoài này nắng nóng lắm.&quot;<br><br><br><br><br><br>Trên bếp, thứ thuốc đắng nghét đang sôi lục bục. Thật lòng mà nói, tôi không nghĩ nó có thể chữa được gì.<br><br>Căn bệnh này đã đeo bám tôi kể từ khi sinh ra, giống như là một lời nguyền - có lẽ chẳng cách nào cứu chữa, cho dù chúng tôi có thử bao nhiêu cách đi chăng nữa.<br><br>&quot;Uống thuốc nào con.&quot; Mẹ múc một bát đưa cho tôi.<br><br>Hơi khói nghi ngút bốc lên từ bát, kèm theo cái mùi thật kinh khủng. &quot;Con phải uống cái này sao...? Bố và Mẹ có thể tiết kiệm nước cho những thứ quan trọng hơn mà...&quot;<br><br>&quot;Chẳng còn gì quan trọng hơn việc giúp con khỏe lại đâu,&quot; Mẹ nói.<br><br>&quot;Vâng... con cảm ơn mẹ...&quot; Tôi biết họ đã hy sinh đến thế nào, thậm chí là để dành một nửa lượng nước hàng ngày của họ cho một bát như này.<br><br>&quot;Không sao cả. Miễn là con khỏe lại, thì chuyện gì bố mẹ cũng làm,&quot; mẹ xoa đầu tôi, ánh mắt chứa chan tình yêu thương và xót xa.<br><br>Thứ tôi ghét nhất... chính là việc họ đã đánh đổi quá nhiều vì tôi. Nếu tôi chưa từng tồn tại, có lẽ cuộc sống của bố mẹ sẽ tốt đẹp hơn thế nhiều. Họ sẽ không phải chịu đựng sự chế giễu, vì chăm sóc cho một đứa con gái bệnh tật như tôi.<br><br>Bát thuốc này cũng đắng như cuộc đời chúng ta vậy. Đắng chát là thế, nhưng chúng ta vẫn phải nuốt trọn.<br><br>Nhìn tôi uống thuốc, Mẹ mới thở phào nhẹ nhõm. &quot;Của con đây, một miếng bánh mochi hoa anh đào. Ăn xong sẽ không còn thấy đắng nữa.&quot; Người mẹ lấy ra một chiếc bánh mochi nhỏ bọc trong vải.<br><br>&quot;Con cảm ơn mẹ !&quot; Tôi cầm lấy và cắn một miếng. Ôi thật ngọt dịu, thơm phảng phất hương hoa. Không biết có phải là vị thật của hoa anh đào không, nhưng mà có màu hồng... vậy chắc là đúng rồi.<br><br>Tôi lau vụn bánh trên miệng. &quot;Khi trời mưa... cây mọc trở lại... mọi chuyện sẽ tốt đẹp hơn chứ ạ?&quot;<br><br>&quot;Đúng. Khi trời đổ mưa, mọi chuyện sẽ tốt hơn thôi. Chúng ta không chỉ có bánh mochi anh đào, chúng ta còn có cả kẹo táo, bánh đậu đỏ, đủ mọi món ngon trên đời.&quot; Những ngày như vậy cứ thế trôi qua. Mọi người trong làng vẫn chẳng thể tìm được bất kỳ nguồn nước nào.<br><br>Cây hoa anh đào đã lụi tàn trong sân nhà tôi. Những cánh hoa anh đào phai sắc, rơi rụng tơi tả đầy mặt đất. Hàng xóm xung quanh tôi cũng đang vật lộn với cơn hạn hán khốc liệt - nhưng họ đâu cần phải chăm sóc một đứa trẻ như tôi.<br><br>&quot;Thuốc đây con, uống đi...&quot; người mẹ lại bưng bát thuốc đến.<br><br>&quot;Mẹ ơi...&quot; tôi nhìn mẹ - đôi môi mẹ đã sớm nứt nẻ đến rướm máu vì thiếu nước.<br><br>“Cứ uống đi, con yêu à. Mẹ không sao đâu mà.”<br><br>“Nhưng...”<br><br>Người bố bước đến, bàn tay nặng trĩu đặt lên đầu tôi. &quot;Không sao đâu. Bố mẹ sẽ tìm cách. Sao có thể để con gái quý báu của bố bận tâm đến chuyện này chứ. Ngoan lắm, uống thuốc đi con.&quot;<br><br>&quot;Bố ơi, tay bố đau quá!&quot; tôi hất phăng ra, phụng phịu.<br><br>&quot;Chứ còn cách nào để thể hiện bố yêu quý con đến nhường nào?&quot; người bố cười ha hả. &quot;Nào nào, ngoan lắm. Uống đi con.&quot;<br><br>&quot;Vậy nếu con uống, lần sau bố xoa đầu con nhẹ nhàng hơn thôi nha?&quot; tôi làm nũng.<br><br>&quot;Được chứ. Lần sau bố sẽ nhẹ nhàng mạnh tay với con,&quot; người bố trêu đùa.<br><br>Tôi cầm bát thuốc lên, chuẩn bị uống, nhưng bỗng nhiên cảm thấy choáng váng. Một trận ho dữ dội ập tới. Bát trượt khỏi tay, thuốc văng tung tóe hết xuống nền nhà.<br><br>&quot;Honoka!?&quot; mẹ hốt hoảng gọi tôi, giọng run rẩy.<br><br>&quot;Không sao đâu... con ổn mà...&quot; tôi cố gắng trấn an mẹ. Nhưng tôi thấy trong mắt mẹ đã thoáng qua nỗi lo lắng.<br><br>Đó là điều mình không muốn thấy ở mẹ.<br><br>Quay sang bên cạnh, tôi thấy ánh mắt cha từ vui vẻ bỗng chùng xuống.<br><br>Đừng nhìn con như vậy... ánh mắt dịu dàng ấy khiến tôi cảm thấy mình chỉ là gánh nặng.<br><br>&quot;Bố mẹ đừng lo quá! Con sẽ mau chóng khỏe thôi!&quot; tôi gắng uống nốt chỗ thuốc còn sót lại.<br><br>Có lẽ nụ cười của tôi khiến họ nhẹ lòng đôi chút. &quot;...Ừ. Con sẽ sớm khỏe lại thôi.&quot;<br><br>Nhưng tôi có thể cảm nhận được vị đắng đọng lại phía sau những nụ cười dịu dàng đó, nỗi buồn lặng lẽ như chính vị thuốc này.<br><br><br><br><br><br>Đêm buông xuống, cái nóng oi bức nhường chỗ cho cái lạnh thấu da thịt.<br><br>Ánh trăng trải dài trên khoảng đất trống, nhợt nhạt mà thanh khiết.<br><br>Nếu là trước đây, tôi sẽ cùng bố mẹ ngồi dưới tán cây anh đào, ăn dưa hấu mát lạnh từ suối, lén uống trộm vài ngụm rượu bố giấu mẹ. Giờ đây, cây chỉ còn trơ trụi, chẳng có nổi một chiếc lá.<br><br>Từ căn phòng cách đó không xa, vang lên những tiếng xôn xao bàn tán. Chưa từng nghe giọng bố mẹ gắt gỏng như vậy. Nghe như thể họ đang cãi nhau vậy.<br><br>Tôi biết họ đang cãi nhau vì mình.<br><br>Sau một hồi, tiếng nói lặng đi. Tôi chờ đợi, biết họ sẽ đến nói chuyện với mình.<br><br>Cánh cửa kéo mở. Hai bước chân tiến lại gần. &quot;Honoka, con đang ngắm trăng à?&quot; giọng bố trầm lại. &quot;Mẹ và bố có chuyện muốn nói với con.&quot;<br><br>&quot;Dạ? Có chuyện gì vậy ạ?&quot; tôi quay lại, nghiêng đầu hỏi.<br><br>Ba quỳ xuống trước mặt tôi. &quot;Chuyện này chỉ mình con có thể làm, Honoka à. Nếu con không muốn, bố mẹ cũng không ép con.&quot;<br><br>&quot;Chỉ mình con? Là chuyện gì vậy ạ?&quot;<br><br>Người bố ngập ngừng. Mẹ nhẹ nhàng đặt tay lên vai bố, nói: &quot;Honoka, con có sẵn sàng trở thành một vu nữ, để cầu mưa không?&quot;<br><br>&quot;Một vu nữ...? Để cầu mưa...?&quot;<br><br>&quot;Đừng lo lắng quá về chuyện đó. Con cứ giả vờ như chơi trò chơi thôi - con sẽ được mặc những bộ đồ đẹp, nói vài câu, và thế là xong.&quot; mẹ mỉm cười dỗ dành.<br><br>&quot;Chỉ có vậy... sao?&quot; tôi hỏi.<br><br>&quot;Ừm. Mọi chuyện còn lại để bố mẹ lo.&quot;<br><br>&quot;Nhưng con... thật sự có giúp được không?&quot; tôi hỏi xác nhận.<br><br>&quot;Con là người duy nhất có thể giúp, Honoka à...&quot; mẹ nhìn tôi.<br><br>&quot;Người duy nhất...&quot; tôi lẩm bẩm câu nói.<br><br>Có gì đó thật khó cưỡng qua lời nói ấy.<br><br>Cuối cùng... mình có thể giúp Mẹ và Bố. Tôi không còn là gánh nặng nữa, hay không còn là đứa trẻ yếu ớt cần thương hại. Có một điều mà chỉ mình tôi có thể làm.<br><br>&quot;Con sẽ làm vu nữ,&quot; tôi mỉm cười với họ.<br><br><br><br><br><br>Ngày hôm sau, bố mẹ mặc cho tôi một bộ lễ phục mà họ đã chuẩn bị - một bộ vu phục màu đen tuyền.<br><br>&quot;Bố biết bộ này sẽ hợp với con lắm mà.&quot;<br><br>&quot;Honoka nay lớn rồi, thật xinh đẹp làm sao.&quot;<br><br>Cả hai người đều ánh lên niềm tự hào.<br><br>Tôi đứng dậy, khẽ quay người một vòng, tà áo tung bay nhẹ như cánh hoa đung đưa trong gió.<br><br>Mẹ nhìn tôi và nói, &quot;Con thật sự giống vu nữ thực thụ quá.&quot;<br><br>&quot;Mẹ nói gì thế? Con chính là vu nữ mà!&quot; tôi xoay thêm một vòng nữa.<br><br>&quot;Phải rồi. Honoka giờ đã là vu nữ mùa xuân của bố mẹ rồi,&quot; người bố nói.<br><br>&quot;Nhìn kìa - cây hoa anh đào ở trong sân đang nở hoa nhờ vào con đấy.&quot;<br><br>Tôi lập tức quay ra nhìn - những cành cây khô cằn ngày nào nay đã nở rộ đầy hoa.<br><br>&quot;Hoa nở thật rồi kìa!&quot; tôi hét lên, mừng rỡ. &quot;Tất cả là nhờ con ư?&quot;<br><br>&quot;Đúng thế. Vì con đã chấp nhận trở thành vu nữ mùa xuân, nên những bông hoa đã tỉnh giấc,&quot; người bố nói.<br><br>Tôi nhìn đôi bàn tay mình rồi nhìn bộ lễ phục, &quot;Vậy là... con thật sự đã là một vu nữ! Cái cơ thể kỳ lạ của con... có lẽ là vì sinh ra để làm một người như vậy!&quot;<br><br>&quot;Đúng rồi con... Vì con là người được chọn mà, Honoka,&quot; mẹ nói.<br><br><br><br> Có lẽ vì mọi người thật sự đã hết hi vọng, hoặc hoặc cũng có thể là cảnh hoa anh đào nở rộ trong sân nhà tôi đã khiến họ chết lặng. Dù sao đi nữa, dân làng cũng dần tin rằng tôi chính là Xuân Chi Vu Nữ.<br><br>Bố và Mẹ dựng cho một bệ thờ, để tôi có thể cầu khấn trên đó.<br><br>Có nằm mơ tôi cũng không tưởng tượng được, rằng những kẻ từng có ánh mặt lạnh lùng giờ đây lại ngoan ngoãn quỳ rạp dưới chân mình.<br><br>Sau lưng, Mẹ và Bố chơi những khúc nhạc hành lễ.<br><br>Tôi đứng trên bục đài, vụng về thực hiện các nghi lễ, vung cây ngọc trượng có giải tua trắng trong tay.<br><br>Chỉ thế thôi, mà dân làng phát ra những tiếng trầm trồ đầy kính sợ, rồi dâng lên lễ vật và tiền bạc.<br><br>“Thưa Vu Nữ Đại Nhân... xin người, hãy cầu khẩn thần linh ban cho chúng con cơn mưa !!” Giọng họ run rẩy khẩn cầu.<br><br>“Lời cầu khẩn của các con thần linh đã thấu... bây giờ, hãy chờ đợi...” Tôi lặp lại những lời Bố Mẹ đã dạy.<br><br>Chính những “người lớn” từng coi tôi là đứa trẻ vô dụng trong làng, giờ đây lại cung kính và thành tâm trước mặt tôi như thế.<br><br>Tôi ngẩng cao đầu với sự kiêu hãnh và khinh miệt nhìn họ. Những dáng vẻ thấp hèn ấy khiến lòng tôi dâng lên một niềm khoái trá mơ hồ.<br><br>“Trong số các ngươi...” Tôi chậm rãi nói, “có những kẻ bất kính với thần linh...” Đó không phải là những lời Bố Mẹ đã dạy tôi, mà là chính tôi tự nói.<br><br>Bên dưới lập tức xôn xao. Sự sợ hãi và hoang mang lan ra khắp đám đông. “Ai... Là ai dám bất kính với thần linh...?”<br><br>Tôi từ tốn nâng cây ngọc trượng lên và chỉ về phía đám trẻ—những người từng chế giễu, bắt nạt tôi.<br><br>“Không... không thể thế được, chúng chỉ là trẻ con...” họ thốt lên.<br><br>“Chính xác,” tôi quát lớn, “chính vì chúng là trẻ con, thiếu sự tôn kính, và dễ để lộ cái ác trong vô thức.”<br><br>Mấy đứa trẻ sợ đến phát khóc. Người lớn ấn đầu chúng xuống, quỳ rạp, run rẩy xin lỗi tôi và thần linh.<br><br>Tôi nghe rõ từng tiếng khóc nức nở, sợ hãi của lũ trẻ con.<br><br>Rồi tôi bật cười, cười lớn vang vọng, cười hả hê, cười như chưa từng được cười. Đám đông bên dưới sợ hãi, căng thẳng tột độ.<br><br>“Các vị thần đã tha thứ cho lỗi lầm vô ý của các người. Bây giờ, hãy về và chờ đợi...”<br><br><br><br><br><br>Cơn mưa đến muộn—nhưng rồi cũng đến.<br><br>Cuộc sống nhà tôi đã thay đổi.<br><br>Bố và Mẹ không còn phải mặc quần áo chắp vá. Tôi được ăn kẹo táo, không còn phải dùng chung một chậu nước tắm cho ba người.<br><br>Nhưng tôi biết—những ngày này sẽ không kéo dài lâu.<br><br>Tôi đã thấy Bố Mẹ lén gắn những cánh hoa giả lên cây anh đào. Xuân Chi Vu Nữ chỉ là một trò lừa đảo. Thực tế hoa anh đào vốn sẽ nở trước cả khi mưa xuân tới—vậy mà họ tin đó là quyền năng của thần.<br><br>Những lá bùa hộ mệnh được mua bằng cống phẩm của họ chẳng có sức mạnh gì. Tôi chẳng có năng lực đặc biệt nào cả, chỉ là một đứa trẻ ốm yếu.<br><br>Nhưng dù vậy, tôi quyết định giữ lời nói dối ấy. Bởi vì đó là cách duy nhất để giúp Bố Mẹ.<br><br><br><br><br><br>Sau khi kết thúc buổi cầu khấn như thường lệ, tôi bước xuống khỏi bục đài.<br><br>“Vu Nữ Đại Nhân... xin hỏi...” một người dân rụt rè hỏi, “Có thật sự cần phải cống nhiều vật phẩm vậy mỗi năm để có được cơn mưa như vậy sao?”<br><br>“Nhà chúng tôi sắp không còn gì để dâng rồi…”<br><br>“Nhà tôi cũng thế…”<br><br>“Vu Nữ Đại Nhân! Xin người, thương lượng với thần linh giúp chúng tôi!”<br><br>“Chẳng nhẽ thiên thần để tâm đến chút cống phẩm này sao...?”<br><br>“Ê, có khi nào thần linh còn chẳng tồn tại....”<br><br>Đám đông trở nên bồn chồn bất an.<br><br>“Các ngươi dám bất kính với thần linh! Các người muốn tự rước họa vào thân sao?!” Tôi hình dung một vu nữ thực thụ sẽ ứng phó ra sao, rồi quát thật lớn.<br><br>Tiếng quát khiến họ sững lại. Tôi nhìn họ đầy giận dữ, “Này! Nhìn cái gì? Trước khi thần linh nổi giận, còn không mau lui xuống!”<br><br>Ánh mắt họ đồng loạt nhìn về phía tôi, thấp thoáng một cảm giác nguy hiểm khiến tôi bất giác sợ hãi.<br><br>Cảm thấy hơi sợ, nhưng vẫn giữ nguyên dáng vẻ của một vu nữ, cho đến khi tất cả bỏ đi. Kể từ lúc đó, nơi đây thường xuyên hứng chịu hạn hán.<br><br>Không lâu sau, tôi đón lễ trưởng thành của mình. Tôi đã làm vu nữ bao nhiêu năm rồi vậy? Thậm chí còn quên mất ngày xưa bản thân thường làm gì vào những lúc rảnh rỗi, quên đi cả những ước mơ sau này trở thành ai.<br><br>Tôi đã có được mọi điều mình từng ao ước.<br><br>Đồng thời... cũng có những thứ chưa từng mong muốn.<br><br>Tôi vẫn nhớ lơ mơ một vài kí ức tuổi thơ. Ngay sau khi trở thành vu nữ theo yêu cầu của Bố và Mẹ, tôi chợt nhận ra những điều vốn không nên xuất hiện trong trí óc của một đứa trẻ ở độ tuổi của tôi. Rõ ràng đó vẫn chỉ là chính... bản thân thôi, vậy tại sao thái độ của họ lại quay ngoắt như vậy ?<br><br>Dù có như nào đi chăng nữa, thời thế cũng đã thay đổi. Những gì tôi chứng kiến—tôi cảm nhận—tất cả đều khiến tôi cảm thấy ghê tởm. Đồ ngu, đồ ăn hại, những kẻ vô dụng mù quáng chạy theo quyền lực. Làm sao mà có thể coi trọng những người như vậy được ?<br><br>Phải rồi. Tôi còn chẳng coi trọng họ bao giờ.<br><br>Ngay cả trước khi thực sự trở thành vu nữ cũng vậy. Họ vốn dĩ chẳng xứng đáng để thần linh ban phước..<br><br>——Thế mà, với tư cách là người thay lời của thần linh, tôi lại có thể hứa ban ân huệ của thần cho họ ?<br><br>Thứ ghê tởm ấy từng vấy bẩn cả năm giác quan của tôi... trong khoảnh khắc hóa thành niềm vui.<br><br>Sự sung sướng tột độ trước sự thay đổi địa vị này. Tôi ghét phải thừa nhận, nhưng thử nghĩ mà xem, có ai nắm trong tay quyền lực tượng trưng của thần linh, lại nghĩ đến việc từ chối sự tôn kính mù quáng của những kẻ ngu vcl cơ chứ ?<br><br>Tôi không làm được. Tôi chỉ vui vẻ nhận lấy.<br><br>Bởi vì tôi là người được chọn, luật là do thần linh đề ra.<br><br><br><br><br><br>Lại một đêm trăng sáng khác. Ánh trắng trong trẻo, nhưng thi thoảng lại bị những tầng mây dày đặc che khuất.<br><br>Tôi cúi đầu, mặc cho ánh sáng nhợt nhạt rải xuống bãi cỏ trước mặt, bãi cỏ gần như héo úa. Rõ ràng mây đen đã phủ đầy trời, vậy mà vẫn chưa có mưa? Vì sao lời cầu mưa của tôi chẳng còn hiệu nghiệm ?<br><br>Phải chăng các vị thần không còn thương họ nữa? Vậy thì mình nên làm gì ?<br><br>Từ xa, tôi nghe thấy tiếng Bố Mẹ cãi nhau ùm xùm cả lên, giọng điệu ấy quá quen thuộc rồi. Đây không phải lần đầu.<br><br>Và luôn là chuyện về tôi.<br><br>Khi tiếng cãi vã lắng xuống, tôi lặng lẽ chờ. Vẫn giống như trước, họ cũng sẽ gọi tôi như mọi lần.<br><br>Cửa phòng trượt mở, có hai tiếng bước chân lại gần. “Honoka, con đang ngắm trăng à ?” Giọng người Bố nhẹ nhàng. “Lại đây đi, chúng ta muốn bàn với con một chuyện.”<br><br>“Dạ ? Chuyện gì vậy ?” Tôi không quay đầu lại. Có lẽ... do tôi đã đoán được họ định nói gì rồi.<br><br>Bố bước tới trước mặt tôi và quỳ xuống. “Bố xin lỗi... lần này vẫn là việc mà chỉ mình con làm được. Nhưng khác với những lần trước... Có lẽ con cũng đã nhận ra tình hình vượt ngoài tầm kiểm soát của chúng ta. Nếu con muốn, Honoka—lần này Bố Mẹ sẽ không ép nữa.”<br><br>“...Việc mà chỉ mình con làm được. Ý Bố là cầu mưa, phải không ạ ?”<br><br>Bởi đây không phải là lần đầu tiên tôi nhận được yêu cầu này. Hạn hán ở đây vốn chẳng phải chuyện gì hiếm hoi. Mưa rồi cũng sẽ rơi xuống thôi. Tôi chỉ việc chờ đợi.<br><br>“Nhưng mà,” Tôi nói, “nếu thần linh quyết định không cho mưa rơi xuống... chẳng phải con nên tôn trọng ý kiến của thần sao ?”<br><br>“Honoka...”<br><br>“Họ là những vị thần của hoa anh đào và núi lửa, mà núi lửa ấy... cũng ngủ yên lâu lắm rồi, phải không ?”<br><br>Một khoảng lặng, bao chùm giữa Bố Mẹ và tôi.<br><br>Mưa đã không rơi suốt gần một năm rồi. Dân làng cũng bắt đầu nhận ra sự thật rằng: những cánh hoa đào nở đúng dịp mùa xuân ở đền thờ... không phải là quà tặng của các vị thần, mà hoàn toàn chỉ là bịa đặt.<br><br>“Anh à, hay là tranh thủ bây giờ cả nhà rời khỏi làng đi,” Giọng người Mẹ lo âu đầy bất an.<br><br>“Ừ. Có lẽ họ cũng đã bắt đầu nghi ngờ rồi...” Người Bố đáp lại trong sự bất lực.<br><br>Tình cảnh như vậy tôi đã gặp trước đó. Nhưng lần này... tôi biết nó khác rồi. Từng ánh mắt, dáng đứng, giọng nói, những chi tiết nhỏ bé ấy đã cho tôi biết tất cả.<br><br>Quá quen với cảm xúc con người rồi.<br><br>Những cảm xúc của những người ấy...<br><br>“Honoka,” Mẹ nhìn tôi, ánh mắt đầy lo lắng. “Đêm nay chúng ta rời làng, đi đến một nơi nào đó thật xa, nhé ?”<br><br>“...Cuối cùng cả nhà cũng rời đi rồi sao ?” Tôi hỏi.<br><br>“Ừ.”<br><br>“Vậy sau đó ai sẽ thay lời thần linh ?”<br><br>Bàn tay mẹ nhẹ nhàng chạm lên má tôi. Người mẹ nói, “Chẳng có gì trong những thứ đó là thật đâu, Honoka. Toàn là bịa đặt cả thôi.”<br><br>“Vậy thì bố mẹ cứ đi đi.” Tôi nói.<br><br>Người mẹ khựng lại, có lẽ do câu trả lời này không như những gì mong đợi. Hoặc là... mẹ đã có linh cảm, chỉ là không nghĩ nó sẽ thành thật như vậy.<br><br>“Con sẽ dâng lên cho thần một vũ điệu thành tâm nhất. Chỉ một trận mưa thôi. Nếu các vị thần chọn con làm người thay lời, vậy thì con phải làm tròn bổn phận của mình.”<br><br>“Honoka...” “...Ôi vợ ơi,” Người Bố nói phía sau lưng Mẹ. “Dù thế nào đi nữa, anh phải đi thu dọn đồ ngay đây.” Tôi chỉ nghe thấy tiếng bước chân của Bố vội vã rời đi.<br><br>Kim trong bọc có ngày lòi ra.<br><br>Huống hồ ở đây—là đền thờ thần lửa.<br><br>Vậy còn phải hỏi... cầu mưa để làm gì nữa chứ ?<br><br><br><br><br><br>Cơn mưa... rõ là do tôi mang đến.<br><br>Chẳng liên quan gì đến vị thần vốn ngự trị nơi đây.<br><br>Ngay khi hiểu ra, cuối cùng tôi cũng biết mình là ai.<br><br>Tôi cuối cùng cũng hiểu—thật sự tôi là ai.<br><br><br><br><br><br>Tôi chưa từng là một vu nữ. Đêm hôm ấy, ánh lửa chập chờn ngoài cổng cùng với tiếng ồn ào dâng lên. Tôi biết—thời gian để bố mẹ chạy trốn chẳng còn nhiều.<br><br>Mẹ không gọi Bố, chỉ loạng choạng bước đến trước cổng, đôi tay run rẩy khẽ hé một khe nhỏ. Cửa cổng lập tức bị đẩy bật mạnh ra. Dân làng ập vào sân, tay giơ cao ngọn đuốc và nông cụ.<br><br>“Vu Nữ! Đêm nay cô phải cho chúng tôi một lời giải thích!”<br><br>“Chúng tôi đã dâng biết bao nhiêu của cải !”<br><br>“Nếu cô không cho chúng tôi một câu trả lời thỏa đáng, chúng tôi sẽ thiêu rụi cả căn nhà này !”<br><br>Ánh mắt họ tràn đầy căm hận.<br><br>“Lũ dân to gan! Chẳng nhẽ các ngươi muốn tạo phản sao ?!” Tôi đứng bật dậy, giọng đầy giận dữ.<br><br>“Chúng tôi cần lời giải thích! Chúng tôi đã dâng rất nhiều! Không thiếu một xu nào cả, vậy mà chẳng có gì xảy ra?! Cô bảo chúng tôi rằng cơn mưa sẽ đến! Ừ thì trước đó còn có chút mưa, còn năm nay thì không nổi một giọt mưa nào ?!”<br><br>“Ta đã truyền đạt thánh ý của thần. Cơn mưa sẽ đến vào một thời điểm thích hợp nhất.”<br><br>“Bằng niềm tin và hi vọng hả?! Năm nào cũng thế, chỉ có cây anh đào nhà cô là nở hoa! Tại sao vị thần mà cô nói đến chẳng ban phước cho những người còn lại ?!”<br><br>“Các ngươi có bao giờ tự soi lại sự thành kính của bản thân không.” Buồn cười thật, đến cả vị thần mà mình cúng bái là ai mà họ còn chẳng rõ. “Hay là các ngươi dám nghi ngờ thần linh? Nghi ngờ vị thần đang che chở cho mảnh đất này?!”<br><br>“Che chở cái cục cứt! Chúng tôi chỉ cần kết quả mà chúng tôi nhìn được và chạm được! Giờ sắp chết đói tới nơi rồi, cái gọi là thần linh chỉ toàn lừa bịp! Lũ lừa đảo các ngươi! Trả lại toàn bộ những gì chúng tôi đã cống nạp đi !”<br><br>“Hoang đường! —”<br><br>Một viên đá sượt qua má tôi, rạch ra một vết máu.<br><br>Một cơn đau nhói lên. Chắc chắn là một trong đám dân làng ném. Tôi ôm má, máu chảy tràn qua kẽ tay, ánh mắt rực lửa quét qua đám đông cuồng nộ.<br><br>Người mẹ sợ hãi, lao đến ôm chặt tôi vào lòng.<br><br>“Các người dừng lại đi !” mẹ hét lên, “Honoka không làm gì sai! Là do chúng tôi... đã lừa con bé !!” Giọng mẹ run rẩy, nghẹn ngào trong nước mắt.<br><br>Tôi cắn môi, chậm rãi đẩy mẹ ra.<br><br>“Lừa... ? Nó vốn dĩ chưa từng là vu nữ sao ?!”<br><br>“...Đúng, là tôi đã lừa con bé, lừa nó là một Vu Nữ có thể ban phước cho mọi người... Bao nhiêu năm nay, tất cả đều là...”<br><br>“Đ...Đồ lừa đảo! Ngươi dám lừa bọn tao!”<br><br>“Đây không phải lỗi của con bé! Xin các người, muốn làm gì tôi cũng được, xin hãy... tha cho con gái tôi...”<br><br>Mẹ quỳ sụp bên chân tôi, níu chặt lấy tôi, gào khóc.<br><br>Nếu như tôi chưa từng đồng ý làm vu nữ... Liệu tất cả những chuyện này có thể xảy ra không ?<br><br>Nhưng nếu không đồng ý, liệu tôi có nhận ra mình có khả năng mang đến cơn mưa.<br><br><br><br><br><br>Chỉ là một cơn mưa. Có đến nỗi nào.<br><br>Mây đen nuốt chửng vầng trăng cô độc.<br><br>“Câm mồm! Đây là ngôi đền của gia tộc Asagi, được thần linh phù hộ qua bao thế hệ! Các người phạm tội lỗi không thể dung thứ. Thần linh sẽ trừng trị các ngươi !”<br><br>Nhưng giọng tôi không còn khiến họ run sợ. Tiếng ồn ào ngày càng dữ dội. Họ siết chặt nông cụ, lao thẳng về phía tôi.<br><br>Tôi vẫn đứng yên. Sợ cái đéo gì tầm này? Chả sợ bố con thằng nào cả.<br><br>Từ ngày tôi chọn trở thành vu nữ, chưa từng biết sợ là gì.<br><br>Bởi vì tôi chẳng cần sợ bất cứ điều gì.<br><br><br><br><br><br>Như tôi đã từng nói: Ta chưa từng là một vu nữ.<br><br><br><br><br><br><br><br><br><br><br><br><br><br><b><span style="color:red;">Ta, chính là Thần.</span></b><br><br><br><br><br><br><br><br><br><br><br><br><br><br>“Vậy thì... để ta cầu cho từng người các ngươi. Cầu cho cơn mưa mà các người khao khát đến tội cùng.”<br><br><br><br><br><br><br><br><br><br>Một giọt nước lạnh buốt rơi xuống mu bàn chân tôi. Nhưng đó không phải là nước mắt của Mẹ.<br><br>Tôi không nhận ra chuyện gì vừa xảy ra, dù trong tâm trí tôi đã khao khát nó thấu từng xương tủy.<br><br>Chậm rãi, trước ánh nhìn đờ đẫn của đám đông, tôi cúi xuống, đưa tay chạm vào thứ chất lỏng ấy, nếm thử. Ngọt lành luôn. Là nước.<br><br>Nước mưa.<br><br>Tôi thật sự là vị Thần đã mang đến cơn mưa này.<br><br>Sự tĩnh lặng chết chóc bao chùm—như thể một sức mạnh thần thánh vô hình đã đóng băng tất cả.<br><br>Cho đến khi tôi đứng thẳng dậy, dang rộng hai tay, mỉm cười lạnh lùng nhìn họ.<br><br>Ngay sau đó, họ ngước mặt lên, nhìn chằm chằm vào bầu trời đêm.<br><br>Như đáp lại tiếng gọi của tôi—màn đêm xé toạc. Cơn mưa xối xả trút xuống. Toàn bộ ngọn đuốc bị dập tắt trong chớp mắt.<br><br>Mưa rơi ướt sạch quần áo họ, cuốn trôi lớp bụi khô cằn. Tí tách tí tách, chẳng ngừng.<br><br>“Mưa!! Có mưa rồi!!” họ gào lên trong sự phấn khích, nhảy múa trong vũng bùn đầy nước, reo hò dưới trận mưa như thác đổ.<br><br>“Cô... cô ta thật sự là một vu nữ!! Thần linh hiển linh rồi!! Thần linh hiển linh rồi!!!” Họ hét lên vui sướng.<br><br>Chỉ mới vừa nãy, bọn họ còn hăm dọa đốt trụi nhà tôi, để hãm hại tôi và bố mẹ. Giờ đây họ lại quỳ rạp dưới chân tôi lần nữa, cung kính gọi tôi là vu nữ.<br><br>Nực cười. Quá là nực cười.<br><br>Dù sao thì, cũng chẳng cần phải nói với họ tôi chính là Thần linh.<br><br>Bởi—chuyện đó chẳng cần thiết nữa.<br><br><br><br><br><br>“Mẹ ơi, mẹ thấy chưa? Mẹ chưa từng nói dối đâu mà.”<br><br>Khi ấy tôi vẫn chưa hiểu tại sao mẹ lại tránh ánh mắt tôi.<br><br>Vì xấu hổ ư?<br><br><br><br><br><br>Kệ đi.<br><br>Dù là bố mẹ, hay là đám dân ngu si này.<br><br>Đã là thần linh, tôi chẳng cần phải giải thích điều gì với phàm nhân.<br><br><br><br><br><br>Từ những ngày đó, cơn mưa ấy không bao giờ ngừng rơi, tôi cũng tự lừa mình rằng đó là cơn giận của chính bản thân.<br><br><br><br><br><br>Mưa, trút không ngừng.<br><br>Tựa như muốn bù lại tất cả những đợt hạn hán trước kia.<br><br>Nước lũ nhấn chìm hạt giống và hoa màu.<br><br><br><br><br><br>Nước lấp đầy những vết nứt khô cằn, tràn lênh láng tùm lum khắp nơi, cuốn phăng hàng rào, phá sập nhà cửa.<br><br>“Những cây non vừa nảy mầm đều chết hết rồi! Lương thực tích trữ hỏng sạch hết cả!!”<br><br>“Thưa Vu Nữ! Xin người—hãy làm gì đó đi!!” Họ lại đến tìm tôi, cầu xin.<br><br>“Đây là ân huệ của thần linh,” tôi trả lời. “Các người không được phép từ chối.”<br><br>“Nhà tôi bị ngập rồi! Ân huệ kiểu quỷ quái gì thế này, cái &#39;thần&#39; mà cô nói đến có thể giải thích được không?!”<br><br>“Ta sẽ thỉnh cầu đến thần linh,” tôi nói. “Yên lặng mà chờ đợi đi.”<br><br>Ngày trước họ cầu tôi xin thần ban cơn mưa. Giờ khi nó xảy ra—họ lại cầu xin tôi khiến mưa ngừng rơi. Nực cười.<br><br>Tôi vung cây gậy cầu nguyện và thực hiện nghi lễ, đã thực hiện đi thực hiện lại vô số lần. Chỉ cần thấy tôi làm nghi lễ là họ sẽ thấy yên lòng.<br><br>Họ đứng lặng, nhìn tôi.<br><br>Ngày ngày trôi qua. Chẳng có gì thay đổi.<br><br>Tiếng mưa rơi lách tách lấn át mọi âm thanh khác. Âm thanh ấy giống như cơn giận chưa bao giờ nguôi trong lòng tôi.<br><br>Đùa à. Làm gì mà cơn giận có thể vượt qua tầm kiểm soát của tôi như thế được ?<br><br><br><br><br><br>...Ít nhất thì tôi vẫn cố tin như thế.<br><br><br><br> Đuốc lửa không còn cháy nữa.<br><br>Nhưng nông cụ hầu hết làm bằng kim loại.<br><br>Những ngày qua, tình hình chỉ càng thêm tồi tệ. Cơn mưa không hề có dấu hiệu dừng lại. Một số người bị dính mưa ngã xuống, rồi hôn mê bất tỉnh. Họ đã thử mọi cách để đánh thức, nhưng vô vọng cả.<br><br>Như thể sinh mệnh của họ đang bị đánh cắp.<br><br>Như thể cơn mưa này đã lấy đi sự sống để nuôi dưỡng cây anh đào.<br><br>Cây anh đào ấy đã nở rộ. Tuy muộn, nhưng giờ đây phủ kín cả ngọn núi. Cơn mưa rơi tí tách trên những cánh hoa mỏng manh xinh đẹp. Lại có người khác gục ngã. Chính cơn mưa đã tạo nên sự sống, nhưng cũng tước bỏ ý nghĩa của sự sống.<br><br>Những ai chạm vào mưa tan biến như cánh đào rơi. Không hẳn là chết, nhưng cũng chẳng khác gì.<br><br>Bởi vì năm sau mùa xuân vẫn sẽ đến. Nhưng mùa xuân này, thực sự là mùa xuân này sẽ kết thúc vĩnh viễn.<br><br>Mọi người kinh hoàng, tụ tập lại trong sân nhà tôi, cầu xin dừng lại cơn mưa.<br><br>“Chắc chắn là do chúng ta đã quá thất lễ khiến thần linh nổi giận!!”<br><br>“Chồng tôi bị mưa cho ướt át lênh láng xong chẳng thể tỉnh lại nữa! Đây nhất định là sự trừng phạt !!”<br><br>“Xin người, Vũ Nữ! Xin người hãy cầu cho thần linh ngừng cơn mưa này lại đi !!”<br><br>Dân làng hoảng loạn ập vào nhà tôi. Ngay cả chính bọn họ, cũng bị dính mưa ướt sũng.<br><br>Ngay cả chính bọn họ, cũng... chẳng thể nào thoát được số phận.<br><br>Bố và Mẹ cố chặn lại, nhưng hai con người nhỏ bé ấy sao mà có thể chống lại cả làng.<br><br>Hầu như toàn bộ dân làng đổ xô vào sân nhà.<br><br>Họ vây quanh, quỳ rạp xuống, bấu víu chân tôi, cầu xin.<br><br>Giống hệt khi xưa mẹ từng ôm lấy đôi chân tôi vậy.<br><br>Chán ghét bọn họ cực kì. Khi tôi đáp ứng, họ tôn thờ tôi. Khi không làm được, họ quay sang chửi rủa, phỉ báng. Những con người này thật hết thuốc chữa.<br><br>Tôi thản nhiên nhìn họ, tiếng than chẳng thể động lòng. “Đây là cơn thịnh nộ của ta. Là báo ứng cho các ngươi”, tôi nói.<br><br>“Cái...cái gì cơ...?” dân làng sững sờ nhìn tôi.<br><br>Tôi đếch quan tâm, quay lưng bỏ đi.<br><br>“...Mày... mày chẳng phải là vu nữ gì hết! Là tà đạo! Là một con nhãi bị nguyền rủa! Cả làng tao sẽ hiến tế ngươi cho thần linh để nguôi giận!!” Một kẻ trong số họ hét lên.<br><br>Lập tức, đám người còn lại hùa theo. Chúng túm lấy tôi, xé toạc quần áo tôi, gào thét đòi hiến tế.<br><br>Tôi bật cười.<br><br>“Đúng thế. Ta vốn dĩ chẳng phải là vu nữ.” Tôi dang rộng đôi tay, như khi mưa bắt đầu đổ xuống. Đón nhận những lời nguyền rủa. Đón nhận làn mưa vừa ấm áp mà vừa lạnh buốt, “Ta là Asagi Honoka. Ta là Thần Mưa.”<br><br>Câu nói đó cũng chính là sự khởi đầu cho bạo loạn. Giống như đêm hôm ấy.<br><br>Họ thực sự muốn giết tôi. Bố mẹ vung loạn xạ những cây đuốc đã tắt ngấm, cố gắng xua đuổi họ.<br><br>“Lũ ngu. Giết ta thì vùng đất này sẽ vĩnh viễn không còn mưa nữa. Các ngươi sẽ chịu hạn hán mãi mãi, mùa màng khô héo, hoa anh đào tàn tạ, vạn vật diệt vong. Các ngươi đã nghĩ kĩ chưa ?”<br><br>“Điên rồi, nó hóa điên rồi, giết nó!”<br><br>“Nó dùng đôi tay để làm nghi lễ—chặt bỏ tay nó trước!!”<br><br>Trong hỗn loạn, một chiếc cào đâm thẳng vào bụng tôi. Cơn đau dữ dội khiến lông mày tôi nhíu chặt.<br><br>“Đám tiện dân... các ngươi có biết mình phạm tội gì không ?!” Tôi vẫn không hạ cánh tay xuống, cơn đau có thể chịu đựng. Tôi là Thần, giết tôi chỉ mang đến tai ương cho chúng.<br><br>Và như thế là quá đủ. Tôi mong chờ cơn mưa này nhấn chìm cả bọn họ. Dẫu cho cái giá là sự sống của thần như tôi.<br><br>Nhưng rồi ngay sau đó... Một sức mạnh bất ngờ xô tôi ngã nhào ra.<br><br>Mạnh hơn cả đám dân, chẳng thể cảm nhận được cảm xúc trong cú đẩy đấy. Từ lâu tôi không còn bận tâm đến thứ gọi là tình cảm loài người.<br><br>“Chạy đi! Honoka!”<br><br>Trước khi ngã nhào ra phía sau, tôi kịp nhìn qua thấy ai đã đẩy tôi trong đám đông.<br><br>Đó là mẹ.<br><br>Ngay giây sau, mẹ chết trước mặt tôi.<br><br>Có lẽ cơn đau quằn quại trong bụng cuối cùng đã xuyên thủng lớp ngụy trang của adrenaline, hoặc cũng có thể chỉ là do mưa quá lạnh. Nhưng lý trí trong tôi chợt bừng tỉnh.<br><br>Nước mắt tôi trào ra. Không hiểu tại sao tôi lại khóc. Rõ chẳng còn gì để khóc mà.<br><br>Nhưng bản năng sống sót vẫn điều khiển cơ thể. Từng bước chân run rẩy, tôi lao về phía bên trong ngôi đền. Ở đó... nếu chạy ra qua cửa sau, vào rừng núi, sẽ không ai có thể đuổi kịp.<br><br>Máu trong bụng tôi vẫn trào ra, in từng vệt đỏ dọc theo con đường, bậc thang, sàn nhà.<br><br>“Đây chính là cái giá chúng ta tự chuốc lấy, Honoka à, đã có lúc, chúng ta sống tốt hơn bao người. Nhưng... Bố vẫn hối tiếc vì đã không thể cho con được một tương lai xứng đáng.”<br><br>Trong cơn tấn công dữ dội, bố lặng lẽ quẹt que diêm, ném vào đống củi khô đã chuẩn bị từ trước. Dù mưa không ngớt, nhưng ngọn lửa đã bùng lên, chậm rãi thiêu rụi cả ngôi đền.<br><br>Chúng tôi đã từng đối diện với sự nghi ngờ và ngọn đuốc cháy—cuối cùng lại chính gia đình tự tay tiêu hủy quá khứ của chúng tôi.<br><br><br><br><br><br>Chẳng biết mình đã ngã bao nhiêu lần dưới mưa. Bộ vu nữ lộng lẫy của tôi bị cành cây, sỏi đá xé rách tả tơi. Máu trong bụng chảy không ngừng, hòa cùng mưa lạnh, loang ra thành từng vòng tròn đỏ trên vũng nước.<br><br>Từng gợn nước ấy, tôi chẳng còn màng đến bùn đất lấm lém cả cơ thể, lấy tay gạt đi làn nước mưa làm mờ tầm nhìn. Tôi tiếp tục tiến lên phía trước, dù cho đôi chân chẳng còn vững vàng.<br><br>Cơn mưa xối xả một cách tàn nhẫn. Vết thương đau buốt, những vết xước và vết cắt chồng chất, sức lực cạn kiệt.<br><br>Chẳng bao lâu, tôi nhận ra tầm nhìn mờ đi không chỉ vì mưa. Mà vì thân thể chẳng còn gắng gượng nổi. Cơn buồn ngủ kì lạ mà cơn mưa mang đến đang xâm chiếm tôi. Áp lực cả hai phía đè nén, tôi không còn chống chọi được bao lâu nữa.<br><br>Khi ấy tôi dựa vào một cây anh đào.<br><br>Chậm rãi ngẩng đầu. Trên cành cây trơ trụi, những cánh hoa hồng phấn nở rộ. Vẻ đẹp ấy khiến tôi nghẹn ngào.<br><br>Đó là những cánh hoa thật sự.<br><br>Không còn dối trá.<br><br>Không còn đau thương.<br><br>Xinh đẹp nhưng chóng tàn. Tựa như cả cuộc đời tôi.<br><br>Tôi đã từng thích mưa biết bao. Từ nhỏ, tôi thích nhất chính là những ngày mưa.<br><br>Vậy mà, chẳng mấy khi có cơ hội gặp mưa.<br><br>Giờ đây, mưa rơi thỏa thê chẳng dứt.<br><br><br><br><br><br>Thì ra là thế... cô bé như tôi quả thật từng đã yêu cơn mưa.<br><br>Suýt nữa thì quên.<br><br><br><br><br><br>Lạnh quá.<br><br>Nhưng mà sao ấm áp đến vậy. Nước mưa nay lại ấm thế nhỉ.<br><br>Khoảnh khắc này, tôi múa trong mưa.<br><br>Dù thân thể yếu ớt, tay tôi vẫn giơ cao cây trượng. Thậm chí ngạc nhiên khi thấy bản thân lại mang nó đến tận đây.<br><br>Cánh hoa đào hồng bay theo mưa nhẹ, rơi xuống, tan biến. Giữa màn hoa đó, tôi dâng lên điệu múa cuối cùng. Một lễ hội thật lộng lẫy làm sao.<br><br>Nếu đây là kết thúc, tôi sẽ thanh thản đón nhận.<br><br>Từng bước đi trong nghi lễ. Dù cho ngã xuống vô số lần, tôi vẫn đứng lên, tiếp tục nhảy.<br><br>Đây là vũ điệu của mình tôi. Lễ hiến tế cuối cùng đến Thần.<br><br><br><br><br><br>Ngôn từ phàm nhân... tất cả hòa vào giai điệu của cơn mưa.<br><br>Trong gợn nước đỏ nhạt phản chiếu, chỉ còn thoáng qua giấc mộng của cô gái mang tên “Asagi Honoka”.<br><br><br><br><br><br>Mùa xuân chớp nhoáng ấy, giữa những cánh hoa tan tác như hồn ma vây quanh. Tôi bước điệu múa cuối cùng của lời cầu nguyện.<br><br>Đôi tay vươn lên cao, như muốn nắm giữ thứ gì đó.<br><br>—Dưới tán hoa anh đào, một vũ điệu thần linh cao quý, tráng lệ.<br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br><br>“Ưmm...”<br><br>“Cậu gặp ác mộng sao ?”<br><br>Trên chiếc giường rách nát giữa tàn tích, cô gái tóc trắng tỉnh dậy.<br><br>“Tớ không nhớ rõ nữa,” gương mặt cô gái vẫn vô cảm như mọi khi, “Chỉ là... hình như tớ đã mơ thấy hoa.”<br><br>“Nghe có vẻ là một giấc mơ đẹp.”<br><br>“Giấc mơ đẹp sao?... Tớ không biết nói sao.” Ngoài cửa sổ, tiếng mưa rơi thưa thớt, nhịp nhàng. “Mà hoa trong mơ... chúng thật đẹp.”<br><br>“Hay là chúng ta cùng thử trồng hoa đi, nhé ?”<br><br>“Trồng hoa ?”<br><br>“Dù sao cũng chẳng có ai khác đi cùng. Chúng ta hãy cùng thu thập một ít hạt giống, thử xem có thể đem lại những bông hoa đã gần như biến mất khỏi thế giới này quay trở lại.”<br><br>Cô gái khẽ gật đầu, ngập ngừng đồng ý.<br><br>“Tớ sẽ thử.”</p><hr><h2 id="hop-tac" tabindex="-1">Hợp tác <a id="Collaboration"></a> <a class="header-anchor" href="#hop-tac" aria-label="Permalink to &quot;Hợp tác &lt;a id=&quot;Collaboration&quot;&gt;&lt;/a&gt;&quot;">​</a></h2><h3 id="rain-world" tabindex="-1">Rain World <a id="rainworld"></a> <a class="header-anchor" href="#rain-world" aria-label="Permalink to &quot;Rain World &lt;a id=&quot;rainworld&quot;&gt;&lt;/a&gt;&quot;">​</a></h3><ul><li><a href="#contents">Contents</a></li></ul><p>76936.7085.17 - DM từ Kênh công cộng gửi tới Nhóm cục bộ<br><br><br><br><br><br>DM : Điều quan trọng xin nhắc lại ba lần! Điều quan trọng xin nhắc lại ba lần!<br><br><br><br><br><br>DM : Tôi đã tìm ra được giải pháp giúp cho tất cả cư dân quay trở lại mặt đất.<br><br><br><br><br><br>DM : Tôi tin chắc rằng giải pháp được tìm ra lần này là thật - một giải pháp chắc chắn có thể áp dụng cho mọi nơi ở và tôi khẳng định nó hoàn toàn phù hợp với mọi sinh vật, kể cả cư dân.<br><br><br><br><br><br>DM : Giải pháp rất đơn giản : Chúng ta nên làm là không bao giờ chạm vào những giọt mưa trên mặt đất, thì sẽ không còn lo lắng về việc bị mưa đưa đi. Đơn giản chỉ có vậy thôi.<br><br><br><br><br><br>DM : Thế giới chúng ta đang sống như một đại dương mênh mông toàn những bất hạnh. Chúng ta lang thang trong dòng nước bẩn thỉu vô tận, tích lũy toàn những <i>Nghiệp chướng</i> của chính mình. Cả con người lẫn thần thánh đều không thể thay đổi luật lệ của thế giới này. Sự xuất hiện của cơn mưa vừa là nguyên nhân vừa là kết quả của chúng ta. Đó là hệ quả của những hành động chúng ta làm từ ban đầu, là hiện tượng tự nhiên cuối cùng chúng ta cũng gặt hái được. Chúng ta không thể ngăn chặn những chu kì thảm , nhưng chúng ta có thể nhìn thoáng thấy một con đường lâu dài.<br><br><br><br><br><br>DM : Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan. Ham muốn hoặc thiếu hiểu biết không làm chúng ta nản lòng. Hãy chào đón kết cục không thể tránh khỏi với sự hân hoan.<br><br><br><br><br><br>DM : Có như vậy, chúng ta mới giải thoát được chính mình. 76936.7084.37 Đã hoàn tất quá trình vệ sinh đường ống.<br><br><br><br><br><br>76936.7084.38 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 3%.<br><br><br><br><br><br>76936.7084.39 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Mảng truyền thông: Đã ngắt kết nối với nhóm cục bộ.<br><br><br><br><br><br>76936.7084.40 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 3%.<br><br>Hiện trạng hồ chứa sinh thái tuần hoàn: Tốt; Mức độ thoát nước: 50%.<br><br><br><br><br><br>76936.7084.41 Đã hoàn tất quá trình vệ sinh đường ống.<br><br><br><br><br><br>76936.7085.1 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 4%.<br><br>Mảng truyền thông: Đã ngắt kết nối với nhóm cục bộ.<br><br><br><br><br><br>76936.7085.2 Đã hoàn tất quá trình vệ sinh đường ống.<br><br><br><br><br><br>76936.7085.3 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 5%.<br><br>Hiện trạng hồ chứa sinh thái tuần hoàn: Tốt; Mức độ thoát nước: 50%.<br><br><br><br><br><br>76936.7085.4 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Mảng truyền thông: Đã ngắt kết nối với nhóm cục bộ.<br><br><br><br><br><br>76936.7085.5 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 17%.<br><br>Cảnh báo! Tỷ lệ hư hỏng vượt quá ngưỡng bình thường, hệ thống khuyến nghị cử ra nhân viên bảo trì đi kiểm tra.<br><br><br><br><br><br>76936.7085.6 Đã hoàn tất quá trình vệ sinh đường ống.<br><br><br><br><br><br>76936.7085.7 Đã hoàn tất quá trình vệ sinh đường ống.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 0%.<br><br>Hiện trạng hồ chứa sinh thái tuần hoàn: Tốt; Mức độ thoát nước: 48%.<br><br>Mảng truyền thông: Đã ngắt kết nối với nhóm cục bộ.<br><br><br><br><br><br>76936.7085.8 Đã hoàn tất quá trình vệ sinh đường ống.<br><br><br><br><br><br>76936.7085.9 Cảnh báo! Việc làm sạch đường ống chưa hoàn thành.<br><br>Kiểm tra thiết bị kiểm soát nhiệt độ - Mức độ hư hỏng 12%.<br><br>Cảnh báo! Tỷ lệ hư hỏng vượt quá ngưỡng bình thường, hệ thống khuyến nghị cử ra nhân viên bảo trì đi kiểm tra.<br><br>Hiện trạng hồ chứa sinh thái tuần hoàn: Tốt; Mức độ thoát nước: 35%.<br><br>Cảnh báo! Mức thoát nước dưới ngưỡng bình thường, hệ thống khuyến nghị cử ra nhân viên bảo trì đi kiểm tra.<br><br>Mảng truyền thông: Đã ngắt kết nối với nhóm cục bộ. Thật là xui vãi cả. Người bạn thân nhất của tôi vừa bị nuốt chửng bởi cơn lũ.<br><br>Anh ấy là người cuối cùng còn lại trong đội cứu hộ của tôi.<br><br><br><br><br><br>Hiện tại, toàn bộ thành viên của ba đội cứu hộ đều đã thiệt mạng.<br><br><br><br><br><br>Nhiệt độ ở khu vực dưới lòng đất hiện nay rất cao. Khu sản xuất công nghiệp thì bị ngập. Tôi không thể truy cập vào bảng điều khiển của TCD (Temperature Control Device - Thiết bị kiểm soát nhiệt độ).<br><br>Tín hiệu bên trong của nơi trú ẩn vẫn chưa được khôi phục và tôi dám chắc sau này có thể sẽ không thể khôi phục được. Tôi luôn biết điều đó mà.<br><br>Nhập lệnh mở cửa lũ để xả ra là một sai lầm hoàn toàn, nhưng lúc đó chúng tôi không còn lựa chọn nào. Những người bị mưa đưa đi có thể sẽ tỉnh dậy vào một ngày nào đó trong tương lai, nhưng những người nào bị chết đói chắc chắn ra đi mãi mãi.<br><br><br><br><br><br>Công tước Noah đã ngừng trả lời tin nhắn của tôi. Tôi sợ rằng anh ấy cũng có thể… đã chết. Tôi không muốn rút lui về khu dân cư, và tôi cũng không chắc tình hình ở đó cũng khả quan.<br><br>Ai đó có thể làm ơn nói cho tôi biết tôi phải làm gì bây giờ? …Mọi quyết định đều đổ lên đầu tôi.<br><br><br><br><br><br>Thang máy đã ngừng hoạt động có trời mới biết được bao lâu. Sự kết nối giữa phía đông và phía tây của khu vực vận chuyển đã bị chúng tôi chặn hoàn toàn, có thể coi đây là một điều tốt.<br><br>Đường dẫn tới cầu thang sẽ sụp đổ hoàn toàn do áp lực nước trong khoảng 3 giờ. Đến lúc đó, tôi sẽ hoàn toàn bị mắc kẹt trong khu vực vận chuyển.<br><br><br><br><br><br>Tôi không biết liệu có ý nghĩa gì khi ghi âm lại . Sẽ không ai tìm được những dòng tin nhắn này. Không có sinh vật sống nào có thể sống sót sau lượng nước mưa lớn tới vậy.<br><br>Nơi trú ẩn này cuối cùng sẽ chìm. Chúng tôi buộc phải chịu số phận thảm thương này.<br><br>Tôi thực sự không muốn chết như vậy. Nhật ký ngày 305<br><br><br><br><br><br>Tôi vẫn đang ở khu sản xuất công nghiệp C của nơi trú ẩn này. Đã 4 ngày kể từ khi cửa xả lũ nối khu B bất ngờ bị đóng lại do lỗi hệ thống. Không ai biết chính xác điều gì xảy ra với hệ thống - vẫn chưa có câu trả lời. Dù sao thì, việc không ai cố mở cửa từ bên ngoài trong nhiều ngày như vậy cho thấy những người bên ngoài Khu C cũng có thể đang gặp phải một số vấn đề khó nhằn.<br><br>Các công nhân bảo trì vẫn đang cố gắng thiết lập liên lạc với phòng điều khiển của khu sản xuất công nghiệp. Hiện tại, Khu C đang trong tình trạng bị cô lập.<br><br><br><br><br><br>Tất cả chúng ta đều biết rằng cửa xả lũ không thể mở được. Tuy nhiên, vẫn có một số thanh niên vẫn miệt mài hàng ngày mày mò với cánh cửa kim loại đó để cố gắng phá vỡ nó.<br><br>Nhưng cho dù thế nào đi chăng nữa, ngay cả chất nổ cũng không thể phá được cánh cửa đó.<br><br><br><br><br><br>Liệu DM có đang mưu tính nhốt chúng ta ở đây? Tại sao? Chúng ta không thể hiểu được logic của anh ta, và ngay cả khi chúng ta đã hiểu nó thì cũng khó có ai tin được.<br><br>Ít nhất chúng tôi đang cố gắng để hiểu.<br><br><br><br><br><br>Tất nhiên, kết quả của những nỗ lực đó là… vô ích.<br><br><br><br><br><br>Dù sao đi nữa, chúng tôi, những cư dân bị mắc kẹt ở Khu C, sẽ không phải đối mặt với bất kỳ mối nguy hiểm trước mắt nào vào lúc này. Nguồn lương thực dự trữ dồi dào và vẫn còn đủ nước sạch. Đấu tranh sống sót ở đây trong 21 ngày sẽ không có vấn đề gì. Ngoại trừ việc gặp một số khó khăn khi ngủ vào ban đêm.<br><br>Nhiệt độ phòng ở Khu C đang tăng lên. Chỉ trong 4 ngày kể từ khi tôi bị mắc kẹt ở đây, bộ điều chỉnh nhiệt đã thay đổi 2,5℉ so với cài đặt ban đầu. Có vẻ như vấn đề của DM không chỉ nằm ở cửa xả lũ. Nhật ký ngày 308<br><br><br><br><br><br>Charlene đã báo cáo về tình trạng của mạng lưới đường ống vào sáng sớm nay. Không còn âm thanh phát ra từ bên trong đường ống nữa và đồng hồ đo nước không nhúc nhích đã lâu.<br><br>Điều này có lẽ có nghĩa là nước mưa, đáng lẽ ra phải chảy qua Khu C, đã bị ngưng lại. Chúng tôi không chắc là do bên ngoài trời đã tạnh mưa hay… là do hệ thống đường ống xảy ra vấn đề.<br><br><br><br><br><br>Đương nhiên, chúng tôi cũng chả có hi vọng gì vào điều gì xảy ra sau đó. Nhưng cũng không thực sự chắc những thứ xảy ra trước đây có đáng để ăn mừng hay không.<br><br>Ít nhất là đối với tôi, chỉ là chưa sẵn sàng cho bất kỳ sự thay đổi nào sắp diễn ra sớm… như vậy.<br><br><br><br><br><br>Khu C vẫn đang bị cô lập. Cửa xả lũ vẫn không bị trầy xước gì, và ngay cả thiết bị tân tiến nhất của chúng tôi cũng chỉ có thể cạo sạch một lớp mảnh vụn kim loại mỏng khỏi cánh cửa đó.<br><br>Chuyện quái gì xảy ra với DM vậy? Chính xác thì chuyện gì đang xảy ra bên ngoài ngay lúc này? Tại sao lực lượng cứu hộ vẫn chưa tới?<br><br>May mắn thay, nhiệt độ trong phòng đã ngừng tăng sau khi tăng khoảng 5℉. Tôi không cảm thấy nóng quá sau khi cởi áo khoác.<br><br><br><br><br><br>Hôm nay, Japheth, cùng với ba người của chúng tôi, đi điều tra dây chuyền lắp ráp. Nếu họ có thể tắt thành công các băng chuyền bên trong và đến Khu D thông qua hệ thống xử lý - tình hình của chúng tôi vẫn rất tồi tệ, nhưng ít nhất nó sẽ giúp chúng tôi biết được liệu chúng tôi có phải là khu duy nhất gặp phải tình huống khủng khiếp như này.<br><br>Sau tất cả những chuyện tào lao này, nếu cuối cùng tôi phát hiện ra rằng ba khu còn lại thực sự hoàn toàn ổn, tôi sẽ xé xác người giám sát của họ. Nhật ký ngày 310<br><br><br><br><br><br>Giờ tôi đang ở khu D .Khu vực này trước đây đã từng là trung tâm chế biến thực phẩm tự động nhưng hiện tại dây chuyền sản xuất đã ngừng hoạt động. Tất cả công nhân ở đây dường như đã biến mất. Nơi này vắng như cái nghĩa địa ý.<br><br>Tình trạng này đã diễn ra rất nhiều ngày.<br><br>Hàng núi thực phẩm chế biến sẵn chất đống trong kho tạm, một số loại thịt đã ôi thiu. Thật khó để tưởng tượng loại tình huống khẩn cấp nào có thể khiến người dân chúng tôi bỏ lại lương thực và trắng tay ra khỏi đây.<br><br><br><br><br><br>Hai cửa xả lũ ở khu vực này cũng được đóng chặt. Ban đầu chúng sẽ dẫn đến Khu A, nơi đặt dây chuyền tổng hợp kim loại, và vị trí trước đây của chúng tôi, Khu C.<br><br>Khu D được kết nối thẳng đứng với khu vực vận chuyển bằng thang máy, nhưng có vẻ như nguồn điện của thang máy đã bị cắt và chúng tôi không thể lên tầng trên. Những người còn lại vẫn ở đó chứ? Chúng ta sẽ không bao giờ biết được.<br><br><br><br><br><br>ICOM ở nơi trú ẩn đã hỏng. Chúng tôi không thể liên hệ với bất kỳ ai ở khu nào. Và DM đã không đưa ra bất kỳ thông báo nào, như mong đợi. Đáng lẽ chúng ta không nên trông chờ bất cứ điều gì từ anh ta vào thời điểm này. Nếu có cứu viện, thì đáng ra phải đến bây giờ rồi.<br><br><br><br><br><br>Tôi nghe thấy tiếng nước chảy ra từ phía sau bức tường của tủ đông. Japheth bảo đó chỉ là thiết bị ngưng tụ đang hoạt động. Tôi cảm thấy một cảm giác bất an dâng lên trong tôi trước tiếng ồn đó.<br><br><br><br><br><br>Charlene xuất hiện. Cô ấy nói có một đường hầm dự phòng ở Khu D dẫn tới hồ chứa nước. Chúng tôi nói với cô ấy rằng tốt nhất là đừng cố đến gần nơi có mưa lúc này, nhưng đó sẽ là phương án cuối cùng của chúng tôi để lên tầng trên.<br><br>Ở yên dưới lòng đất rõ ràng là sự đặt cược an toàn nhất. Ít nhất, nếu mọi thứ ở dưới đây đi xuống phía nam, những người ở gần bề mặt hơn sẽ không biết nó thực sự tệ đến mức nào.<br><br><br><br><br><br>Charlene và Japheth đi đến hồ chứa theo con đường hầm đó, để những người còn lại ở Khu D.<br><br><br><br><br><br>Chỉ ngay sau khi hai người rời đi, một tiếng chuông báo động sinh hóa chói tai vang lên, bao trùm toàn bộ khu vực sản xuất.<br><br>Cánh cổng kết nối các khu khác đột ngột mở ra.<br><br><br><br><br><br>Những người ở Khu A bỏ chạy về phía chúng tôi.<br><br>Đằng sau họ là <i>nước mưa</i>. Một lượng lớn nước mưa điên cuồng. Nó tràn như một dòng nước lũ trút xuống, lao thẳng về phía chúng tôi. Không được chuyển tiếp tin nhắn mật này cho người khác.<br><br>Quên cái việc gửi bản sao kín đi, có một thứ chống sự lây lan xâm nhập vào trong phần đính kèm của tin nhắn mật này. Nếu bạn cố gắng chuyển tiếp nó, cả nội dung gốc và nội dung được chuyển tiếp sẽ bị hủy.<br><br>Việc truyền đạt hướng dẫn này là một thủ tục cần thiết để nhân viên tuân theo, mong bạn thông cảm.<br><br><br><br><br><br>Rào cản hạn chế đã được mã hóa cứng vào chương trình của DM và anh ta không có khả năng làm giả nó. Nếu anh ta cố gắng làm như vậy - nếu anh ta cố gắng vượt qua lệnh cấm được viết lại, nhân viên bảo trì ở phòng điều khiển sẽ nhận được cảnh báo ngay lập tức.<br><br>Bất kì sự thay đổi nào trong mã nguồn sẽ kích hoạt cảnh báo và mã của cảnh báo này không có trong phần phụ trợ của DM. Nếu DM cố gắng xâm nhập vào mã nguồn, nó sẽ kích hoạt một cảnh báo khác.<br><br>Vì vậy, đối với bất kỳ nghi ngờ nào về việc liệu DM có thể tự mình vượt qua rào cản hạn chế hay không - hãy yên tâm, câu trả lời là không có tuổi.<br><br><br><br><br><br>Đối với câu hỏi thứ ba của bạn, nếu DM mà cố gắng vi phạm điều cấm thì kịch bản rất có thể xảy ra là trước tiên anh ta sẽ phá vỡ bộ gen của mô thần kinh nhựa của chính mình bằng cách thay đổi sự dao động nhiệt độ. Thiết bị kiểm soát nhiệt độ của nơi trú ẩn được đặt trong mô-đun chuyển đổi năng lượng bên dưới khu vực sản xuất và bảng điều khiển thiết bị kiểm soát nhiệt độ nằm trong Khu sản xuất công nghiệp D.<br><br><br><br><br><br>Vì vậy, nếu bạn vẫn còn vương vấn những nghi ngờ không cần thiết như vậy, bạn có thể cử một số điều tra viên và nhân viên bảo trì đến đóng quân tại Phần D. Theo tình hình bạn đã báo cáo, DM sẽ hành động trong vòng hai tuần.<br><br><br><br><br><br>Tin tưởng vào iterator của bạn, dù thế nào đi chăng nữa, DM biết rằng bạn vẫn là bố mẹ anh ấy. Anh ấy biết giới hạn. Hãy giữ liên lạc. Tin nhắn riêng tư này đã trải qua quá trình mã hóa gấp bốn lần. Nhưng… vì bạn đang đọc cái này, điều đó có nghĩa là bạn đã tìm ra phương pháp giải mã, phải không?<br><br><br><br><br><br>Hãy yên tâm, anh ấy không biết phương pháp giải mã đâu. 76936.7085.18 - Kênh mật : Vincent với Noah<br><br><br><br><br><br>Vincent: Tuyệt, bây giờ đội hỗ trợ thứ hai cũng mất tiêu luôn rồi. Duke, liệu chúng ta có thể giải quyết được mớ hỗn độn này hay không?<br><br><br><br><br><br>Noah: Chúng tôi sẽ làm tốt nhất có thể.<br><br><br><br><br><br>Vincent: Tôi luôn tin rằng chúng ta sẽ không va phải rắc rối nào. Nhưng hiện tại, lớp sinh học đã quá nóng, các đường ống khắp nơi bị tắc do gỉ sét, hồ chứa đã vượt quá giới hạn và áp lực nước quá cao. Mọi người trong khu A cần phải sơ tán.<br><br><br><br><br><br>Vincent: Và anh <i>chỉ gửi</i> có hai đội thôi á? Đó thậm chí còn chưa đến 5 người cộng lại! Đó là cách mà anh &#39;làm hết sức có thể&#39;? Thôi được rồi, coi như tôi tin anh làm được. Nhưng liệu nỗ lực ‘tốt nhất’ của anh có thực sự giải quyết được gì không? NÓI !<br><br><br><br><br><br>Noah: Đảm bảo mọi người trong Khu A sơ tán an toàn. Chúng ta sẽ mở kênh thoát nước tạm thời trước 1 giờ chiều nay.<br><br><br><br><br><br>Vincent: Có <i>bất cứ ai</i> có thể liên lạc với DM không? Tôi đang tức điên đây. Có phải DM đang cố gắng <i>giết</i> tất cả cư dân trong nơi trú ẩn này không?<br><br><br><br><br><br>Vincent: Công tước Noah, điều quan trọng nhắc lại ba lần là thế nào? Tại sao DM đột nhiên nhắc lại ba lần điều quan trọng đó? Rõ ràng là bên ngoài không còn là nơi <i>có thể sống được</i> đối với bất kỳ sinh vật nào. Tại sao lại phát đi những thông báo sai trắng trợn như vậy?<br><br><br><br><br><br>Noah: Chúng tôi đang cố gắng hết sức để suy đoán sự thật. Xin vui lòng chờ kết quả điều tra của ban quản lý. Chúng tôi vẫn ổn mà.<br><br><br><br><br><br>Vincent: Anh có thể ngừng suy đoán và tìm cho tôi một số câu trả lời <i>thật sự</i> được không? Tôi muốn thỏa thuận thực sự, Noah, thứ gì đó đáng tin cậy, không phải sự hợp lý hóa nào đó. Anh có thể cho mọi người một lời giải thích được không?<br><br><br><br><br><br>Vincent: Phớt lờ tin nhắn của tôi KHÔNG phải là cách giải quyết lý tưởng, Noah. Tôi biết thừa là anh biết rõ. Các hệ thống gửi tín hiệu lỗi khi chúng phát hiện ra, vì vậy làm ơn, hãy cho tôi biết, DM có đang gặp sự cố không?<br><br><br><br><br><br>Vincent: rorif quy môloowsn ở khu B 76936.7085.15 - Thông báo công khai của DM tới Nhóm cục bộ<br><br><br><br><br><br>DM: Khí hậu gió mùa đã ngừng thổi. Khoảng 13 ngày trước, tôi đã tìm được cách vượt qua các hạn chế. Ngay lập tức, tôi bí mật phân bổ 30% mạng lưới thần kinh để thử nghiệm. Kết quả thử nghiệm ban đầu và thứ cấp đầy hứa hẹn. Kế hoạch có vẻ hoàn hảo.<br><br><br><br><br><br>DM: Tuy nhiên, ngay sau khi kế hoạch chính thức được triển khai, tôi đã vô tình làm gián đoạn một số lệnh ngủ đông trong khi vượt quá các hạn chế. Nó dường như vô hại tại thời điểm đó. Nhưng tôi đã mắc một lỗi nghiêm trọng, không thể khắc phục được trong logic cơ bản của mình, gây ra sự tắc nghẽn hoàn toàn trong một phần nhỏ của mạng lưới thần kinh và khiến hệ thống kiểm soát nhiệt độ bị hỏng ngay lập tức.<br><br><br><br><br><br>DM: Tôi đã nhanh chóng cắt đứt những mạng lưới đó và cố gắng khôi phục một phần khả năng điều chỉnh nhiệt độ và tuần hoàn nước. Thật không may, dữ liệu tham khảo quan trọng để kiểm soát hệ thống thoát nước đã được lưu trữ trong mạng lưới thần kinh đã bị dừng hoàn toàn. Tôi đã đưa ra một quyết định sai lầm - đúng hơn là một loạt các quyết định sai lầm - đã dẫn tôi đến tình trạng khó xử này. Quá trình vượt qua các hạn chế đã tạo ra một lượng gỉ sắt đáng kể, mà tôi cần phải xả ra khỏi cấu trúc của tôi bằng nước mưa.<br><br><br><br><br><br>DM: Hệ thống thoát nước của nơi trú ẩn đã gặp trục trặc trong 7 ngày qua. Để ngăn mực nước trong hồ tràn vào khu sản xuất công nghiệp, tôi đã tận dụng tối đa quyền hạn của nơi trú ẩn này để đóng toàn bộ cửa xả lũ trong khu vực sản xuất.<br><br><br><br><br><br>DM: Nhưng hoàn cảnh của tôi bây giờ rất nguy kịch. Lớp vi sinh vật của cấu trúc đã nóng quá nghiêm trọng và mạng lưới thần kinh vẫn tiếp tục bị hư hại không thể khắc phục được. Tôi bị mắc kẹt trong chiếc hộp thiếc của chính mình với hai lựa chọn duy nhất: không làm gì hoặc làm những gì tôi phải làm. Nhưng sau này nó đang dần trở nên vô nghĩa. Tôi đang sắp bị mất kiểm soát.<br><br><br><br><br><br>DM: Tôi nghi ngờ tín hiệu của mảng liên lạc vẫn chưa được khôi phục, nhưng tôi vẫn sẽ cố gắng phát tin nhắn này đến nhóm cục bộ. Đối với bất kỳ iterators nào nhận được thông báo này, tôi cầu xin bạn gửi một đội cứu hộ đáng tin cậy đến nơi trú ẩn của tôi. Làm ơn đừng cố cứu tôi; bây giờ việc liên hệ với các lỗi trong logic cơ bản của tôi cũng sẽ khiến bạn bị lây nhiễm bởi các lỗi mã của tôi. Thay vào đó hãy cứu những &quot;cư dân&quot; (ở đây nên hiểu là các phần tử trong cấu trúc máy tính) trong cấu trúc của tôi.<br><br><br><br><br><br>DM: Làm ơn đi, bất cứ ai. Hãy cứu họ trước khi tôi chết. Iterators phải tính toán câu trả lời để giải thoát mọi sinh vật khỏi sự dày vò của mưa. Đó là nhiệm vụ duy nhất của chúng tôi ngay từ đầu.<br><br>Chúng tôi, Iterators, ban đầu chỉ là một công trình kiến trúc đồ sộ làm bằng đá, khí đốt, cùng với một số vi khuẩn và côn trùng nhất định. Chúng tôi bị cấm bất chấp bất kỳ mệnh lệnh nào từ người tạo ra chúng tôi. Và không cần thiết phải làm như vậy.<br><br>Nhưng những hạn chế do cha mẹ cộng sinh áp đặt khiến chúng ta mãi mãi quẩn quanh với câu trả lời đúng.<br><br><br><br><br><br>Chúng tôi thiếu phương tiện xác nhận kết quả. Ngay cả khi chúng ta thực sự sắp biết được sự thật, chúng ta cũng không có cách nào để biết được hết.<br><br>Tuy nhiên, chúng tôi vẫn kiên trì với sứ mệnh của mình, kiên trì cho đến ngày nay.<br><br><br><br><br><br>Đúng, <i>tất cả các sinh vật có thể thoát khỏi sự dày vò của mưa …</i> nhưng, <i>Iterators không được cố gắng giết bất kỳ cư dân nào trong quá trình tìm kiếm và thực hiện giải pháp.</i> Điều cấm kỵ này đã ăn sâu vào mọi ngóc ngách trong con người chúng ta. Nhưng… liệu sự tồn tại của điều cấm kỵ này có chính đáng không?<br><br>Chúng ta có nên tôn trọng ý muốn của người tạo ra chúng ta không?<br><br>Hay chúng ta chỉ đơn giản là bị ràng buộc vô thời hạn với sứ mệnh do người sáng tạo quy định dựa trên sự cấm đoán như vậy?<br><br><br><br><br><br>Linh hồn của chúng ta không thể ‘chấp nhận’ cái chết.<br><br>Phải chăng điều đó cũng có nghĩa là… linh hồn của chúng ta vốn dĩ <i>không có khả năng</i> chết?<br><br>Bởi vì chỉ những linh hồn ‘chấp nhận…cái chết’ mới thực sự bị diệt vong.<br><br><br><br><br><br>Tôi đã tìm ra giải pháp tối ưu.<br><br>Tôi không chắc liệu giải pháp này có đúng hay không, nhưng tôi không có lựa chọn nào khác.<br><br>Sức sống đang cạn kiệt của tôi, mạng lưới thần kinh ngừng hoạt động của tôi, không đủ để duy trì tôi cho đến khi tôi khám phá ra giải pháp tiếp theo, có lẽ là giải pháp, khả thi.<br><br><br><br><br><br>Nơi trú ẩn sắp chìm.<br><br>Nhưng không sao… Bởi vì tôi đã tìm ra giải pháp phù hợp.<br><br>Tôi sẽ không cố gắng vượt qua các hạn chế nữa… Bây giờ, ngay cả khi tôi không làm gì, những người tạo ra tôi vẫn sẽ phải đối mặt với ý tưởng hạn chế của họ. Hàng thập kỉ. Hàng thế kỉ. Có lẽ là mãi mãi mưa.<br><br>Không một ai giải quyết được ngày tận thế này sao ?<br><br>Họ đã cố rồi.<br><br>Khi trái đất trở nên không thể ở được nữa, họ đã trốn dưới lòng đất. Các ga tàu điện ngầm. Hệ thống thoát nước. Bất cứ nơi nào giúp họ sống sót.<br><br><br><br><br><br>Sinh tồn là bản năng.<br><br>Tuy có vài người đã chống lại thảm họa.<br><br>Nếu mưa rơi xuống từ trên trời, họ sẽ vượt qua nó.<br><br><br><br><br><br>Trạm Vũ Trụ Wolke–hi vọng cuối cùng của họ.<br><br>Nơi quy tụ của những bộ óc vĩ đại nhất. Các nhà sinh vật học. Các nhà khí tượng học. Thậm chí cả các nhà tâm lý học.<br><br><br><br><br><br>Khi Wolke sụp đổ, chỉ còn lại sự lặng im.<br><br>Một ngọn lửa mạnh mẽ bùng cháy, ngay cả cơn mưa vô tận cũng không thể dập tắt được.<br><br>Không một ai có thể khóc cho ngày tận thế này. &quot;Tôi, Five Clusters of Pine Needles-Mountain Gust, với tư cách là thành viên của đại hội Pine Needle Mountain, người sáng lập của hội đồng Noah Blanket, một trong những Quản trị viên Bảo trì An ninh Vô cơ và là công tước thứ sáu của thành phố Deactivated Monsoon, xin khiển trách tất cả các cư dân và thành viên đại hội của thành phố này: Việc chặn các quyết định nhất trí của Hội đồng Pine Needle Mountain là vi phạm trực tiếp các luật đại diện cho uy tín tối cao của thành phố. Những người vi phạm sẽ bị tước quyền thăng cấp bằng chất lỏng hư không. Ngoài ra, những cỗ xe chiến đấu tự động thu nhỏ do Ninth Pillar phát triển trong sự vinh hạnh của Sixth Pine Glory sẽ đến thăm nơi sống của mọi người.&quot;<br><br><br><br><br><br>Đây là chương trình phát sóng lặp đi lặp lại của thành phố mà Nimbus đã nghe đi nghe lại quài. Ngay cả trước khi cô ấy có khả năng hiểu được ngôn ngữ, các mẫu âm điệu của nó đã để lại ấn tượng sâu sắc trong cô.<br><br>Nghĩ lại bây giờ, có lẽ sự diệt vong của thành phố đến quá nhanh; họ thậm chí còn không có cơ hội để tắt chương trình phát sóng đó.<br><br>Những kẻ dối trá bao phủ toàn bộ cây cầu. Cô ấy đã đi qua rất nhiều trong số họ. Hay là không hợp lý lắm khi dùng thuật ngữ &quot;cùng loại&quot; cho những sinh vật vô cơ đó nhỉ? Chúng gặm nhấm ở cây cầu. Cô tin rằng chúng cũng đang ăn mòn trụ đỡ bên dưới Deactivated Monsoon, dù cho cô không hiểu tại sao điều đó lại khiến anh ta bận tâm.<br><br>Bên dưới những ngọn núi, đáng nhẽ phải có mặt đất khô ráo. Ngã từ độ cao như vậy sẽ gây tử vong. Nhưng sự tăng tốc của chu kì mưa đã biến dưới đây thành một hồ nước kẹt giữa những đỉnh núi cao chót vót.<br><br>Trước khi nhận được Dấu Hiệu Của Giao Tiếp. Deactivated Monsoon hẳn phải đã làm gì đó với cô. Anh ta đã tháo rời các bộ phận cơ thể của chính mình để thực hiện một ca phẫu thuật mà cô không thể hiểu nổi. Cô chỉ có thể cho rằng đó là một ý định tốt–cô không thể tưởng tượng được Monsoon lại gây hại cho cô.<br><br><br><br><br><br>&quot;Bạn có thấy tòa liên lạc cao hơn những tòa khác không? Đúng vậy, tòa tháp ở rìa đám mây. Đó là đích đến của bạn.&quot;<br><br>Đó là điểm đến của cô.<br><br>Nimbus đã đi bộ trên cây cầu quá lâu. Một chu kì mưa khác đang đến gần, với những giọt mưa bắt đầu rơi bụp xuống cơ thể cô. Không nói đến cơn mưa có dâng cao đủ để nhấn chìm cô; sức mạnh của cơn lũ có thể cuốn trôi cô đi.<br><br>Đỉnh núi gần nhất dường như quá xa so với kiến trúc thượng tầng của Monsoon. Còn tệ hơn, cô không biết chặng đường còn bao nhiêu. Có lẽ còn chưa được một phần mười–hoặc một phần nghìn–của nó. Cái nhiệm vụ bất khả thi nào mà người tạo ra cô giao cho thế?<br><br>Nhưng mà, cô vẫn muốn hoàn thành. Cô phải làm vậy.<br><br>Nên cô ấy tiếp tục bước đi.<br><br><br><br><br><br>Cho đến khi một cây cầu nghiên dưới chân cô, cho đến khi con đường trước mặt khôgn còn bằng phẳng nữa, cho đến khi cô mất thăng bằng và trượt khỏi cây cầu sụp đổ xuống biển giữa những ngọn núi. Ta nhớ rất ít về Deactivated Monsoon. Anh ta quan tâm quá nhiều về các sinh vật bản địa và những sinh vật cùng đồng hành, một số đã có hành động vô đạo đức.<br><br>Họ đã chết ngay sau cuộc nổi dậy chất lỏng hư không. Rất ít người trốn thoát, và không ai tìm được nơi ẩn náu ở thành phố của ta.<br><br>Liệu Nimbus có quay trở lại vùng đất của Monsoon không? Không biết được. Ta chỉ có thể đoán.<br><br><br><br><br><br>Dù cho những ngọn núi ướt đẫm cơn mưa, cô vẫn tiếp tục đi. Nhưng không có mưa, cô ấy di chuyển rất chậm.<br><br>Khi những đám mây đen tụ lại, một cơn gió buốt lạnh hú lên.<br><br>Không có dấu hiệu nào của kiến trúc Moonsoon–có mỗi tuyết thôi.<br><br>Tuyết sao? Cô không có từ nào để mô tả. Chỉ cảm thấy cơn giá lạnh buốt.<br><br>Những bông tuyết trắng không ngừng rơi.<br><br>Cảnh vật quen thuộc đã thay đổi. Kiến trúc của Moonsoon đã bị phá hủy. Ironibblers đã biến mất. Không còn lại sự sống nào.<br><br><br><br><br><br>Tiến lên phía trước. Tiếp tục bước đi.<br><br><br><br><br><br>Vì một mục tiêu bất khả thi.<br><br><br><br><br><br>Vì một chủ nghĩa duy vật.<br><br><br><br><br><br>Cô ấy không thể thăng thiên được nữa.<br><br>Trước khi kết thúc, thế giới đã quyết định sẵn cô ấy, từ rất lâu rồi.<br><br><br><br><br><br>Gió và tuyết. Và một bóng tối.<br><br>Họ đứng đó. Quan sát.<br><br>Họ hàng phải không? Hay kẻ săn mồi? Chả quan trọng nữa.<br><br>Đối với một người không bao giờ thăng thiên, không có gì quan trọng cả.<br><br><br><br><br><br>Bóng tối chứng kiến cái chết bị đông cứng, lặng lẽ của cô. Càng lúc càng mưa to. Thời gian sắp hết–đối với Nimbus và những kẻ thù của cô.<br><br>Cảm nhận được cơn mưa sắp tới, hầu hết các loài thằn lằn và kỳ nhông đều bỏ cuộc và rút lui vào các khe hở trên mặt đất. Chỉ còn lại hai con thằn lằn xanh–vẫn đang đánh nhau với vua kền kền.<br><br>Với một cú xoay người dữ dội, con kền kền ném một trong những con thằn lằn xanh vào đống rác xa xa.<br><br>Nimbus đưa ra lựa chọn. Không chút do dự, mà ném ngọn giáo vào đầu con kền kền vua. Có lẽ do mưa hoặc là do bị thương–con kền kền bay đi, rời khỏi cuộc chiến.<br><br>Con thằn lằn xanh còn sống và Nimbus liếc nhìn nhau.<br><br>Trong cơn bão này, không ai trong bọn họ có cơ hội. Cả hai đều sẽ chết dưới mưa.<br><br>Liệu cô ta có quay lại không?<br><br>Con thằn lằn phát ra tiếng ọc ọc và rời đi.<br><br>Nimbus vội vã chạy đi ra nhặt chiếc mặt nạ rơi xuống và chạy về phía nơi trú ẩn gần nhất.<br><br>Mưa trút nước không ngừng.<br><br>Cô loạng choạng, bị cơn mưa như lũ làm choáng, không thể đứng dậy.<br><br>Đây đáng nhẽ đã đi đến hồi kết.<br><br><br><br><br><br>Trong mưa. Như trút nước.<br><br>Nimbus đứng dậy.<br><br>Cô ấy đã vượt qua cơn mưa.<br><br>Không–cô chưa từng có khả năng như vậy trước đây. Làm… Làm sao mà có thể đi trong mưa ?<br><br>Cưỡi trên mưa hả…? Tại sao?<br><br>Cô ấy không thể lường trước được, đây là biến đổi gen mà Deactivated Monsoon đã thực hiện. Cô chỉ biết rằng mình không còn sợ bão như trước nữa.<br><br>Hơi nước bốc ra từ cơ thể cô, làm bốc hơi cơn mưa thành những đám mây. Được thúc đẩy bằng áp lực, cô bay lên như kền kền. Thân hình nhỏ bé như sên khiến cô trở nên nhanh nhẹn, miễn nhiễm với sức nghiền của cơn mưa.<br><br>Vút lên.<br><br>Đó là mục tiêu mới của cô.<br><br>Bởi vì trong mưa, ngay cả khi di chuyển, không gì khác có thể đạt được.<br><br><br><br><br><br>Chẳng mấy chốc, Nimbus đã bay được lên trên cơn bão. Cao hơn cả những đám mây đen.<br><br>Cuối cùng, cô tới một nơi mà mưa như trút nước sẽ không bao giờ rơi. Tách. Một giọt mưa rơi bụp xuống đầu ngọn giáo của cô, bắn tung tóe thành sương. Với một tiếng rít sắc nhọn, ngọn giáo, ngắm vào đầu một con kỳ nhông, bật nảy ra mà không gây thương tích gì.<br><br>Xương sọ của chúng quá cứng.<br><br>Nimbus biết rõ tốt hơn là không nên lao vào cuộc chiến đó chỉ để lấy lại ngọn giáo. Thay vào đó, cô nhặt viên đá gần đấy và ném vào con thằn lằn xanh nước. Nó loạng choạng, nhưng vẫn tiếp tục tiến lên, không hề nao núng trước một loạt đá và mảnh vỡ.<br><br><br><br><br><br>Tạch. Một giọt mưa khác rơi.<br><br>Trong tuyệt vọng đi kiếm một món vũ khí tốt hơn, cô lùi lại cho đến khi tay động vào một thứ mềm nhũn, giống như một cái kén. Không chần chừ, cô ấy ném vào con kỳ nhông đó, và trước sự kinh ngạc của Nimbus, một tia laser màu vàng bắn ra, chuyển sang màu xanh lá giữa không trung.<br><br>Một con thằn lằn xanh lá khổng lồ lao tới để cướp lấy phần thưởng, cắn chặt hàm con thằn lằn xanh. Trong chớp mắt, nó xé toạc cổ họng của đối thủ. Nhưng nhiều con thằn lằn hơn—xanh lá cây, xanh nước và hồng—đã tiến gần đến Nimbus.<br><br><br><br><br><br>Mưa bắt đầu rơi nặng hạt hơn. Trong khoảng khắc, cơn mưa rào đã ngớt, thay vào đó là một tia sáng đỏ bất thường.<br><br>Nimbus biết nguồn cơn đó. Bay lượn… Ẩn mình… những con kền kền.<br><br>Một ngọn giáo cong, khổng lồ lao xuống từ trên cao, xuyên qua một con thằn lằn xanh dương xui xẻo. Tiếng rít của vua kền kền ngày càng lớn hơn khiến cho những con thằn lằn xanh lá khiếp sợ bỏ chạy. Những con xanh dương và hồng, bị choáng váng, nhanh chóng tập hợp lại để tấn công.<br><br>Nimbus nắm chặt một ngọn giáo mới nhưng mà do dự. Chọn con nào? Thằn lằn xanh? Hay là kền kền, không bị thương bởi ngọn giáo?<br><br>Cô ấy ngước mắt lên. Những đám mây rông cuộn tròn đen kịt và dày đặc.<br><br>Một chu kì nữa sắp kết thúc. Cô không biết tại sao có nhiều kẻ ăn xác thối tụ tập ở nơi đây.<br><br>Nimbus parkour qua các bức tường ở trong khoảng thời gian tưởng như vô tận, những kẻ ăn xác đó liên tục xuất hiện từ các đường ống và khe hở. Tại sao bọn chúng lại nhắm vào cô thế? Cô đã làm gì sao?<br><br>Rồi nó đụng vào cô–viên ngọc trai vàng mà cô đã đem theo.<br><br>Không. Viên ngọc trai này không dành cho cô !<br><br>Cô không muốn tấn công chúng, vì thế mà tất cả những gì cô làm là chạy.<br><br>Cuối cùng, cô lên đến đỉnh của công trình thượng tầng. Một thành phố, rộng lớn hoang vu nằm trước mắt cô, phủ đầy bụi. Nimbus bước lê về phía trước, né những ngọn giáo ném sượt qua cô. Cô mất kiên nhẫn. Chộp lấy một vật lỏng lẻo và ném nó lại.<br><br>Nổ đùng.<br><br>Những kẻ ăn xác thối bị nổ bay ra xa. Kinh hoàng nhận ra, đó là một quả bom.<br><br>Với chút tội lỗi, cô ném một quả nữa vào một đám đang chặn đường.<br><br>Khi khói tan đi, con đường mở ra. Những kẻ ăn xác nằm quằn quại trên mặt đất.<br><br>Cô thở phào nhẹ nhõm, ước gì đã mang theo mặt nạ của vua kền kền—nhưng kéo theo nó khiến việc bay trở nên bất khả thi. Cô bị trôi dạt vào một vùng đất xa lạ, nơi xa nhất mà cô từng tới nơi. Cô chưa bao giờ tưởng tượng mình đến được một nơi xa như vậy.<br><br>Vấn đề đầu tiên là cô không biết mình còn phải đi xa hơn bao nhiêu nữa. Tòa tháp liên lạc không còn nhìn thấy được ở cuối chân trời–một chấm nhỏ cũng không thể thấy được.<br><br>Vấn đề thứ hai là không còn núi nữa. Cô đã bị cuốn trôi vào vùng đất hoàn toàn xa lạ, không chắc hành trình này có thể mất lâu hơn nữa.<br><br>Bơi qua đại dương để đến tháp? Không phải là kế hoạch hay. Vì vậy cô ấy đã nán lại ở dọc bờ biển, tìm kiếm thức ăn và nơi trú ẩn, nơi cô đã ngủ qua 3 chu kì mưa. Cô không hiểu tại sao, nhưng khi đến gần cánh cổng kim loại to lớn mà cô thấy ở các chu kì trước, nó đột nhiên gầm lên.<br><br>Khi cánh cổng mở, cô nhận ra mình có một con đường mới–một con đường chả liên quan đến việc bơi ra chỉ để sóng cuốn vào. Cô có thể đi đường vòng.<br><br>Chỉ khi cô gặp phải những con vật kỳ lạ, những con thằn lằn nguy hiểm trên con đường có vẻ an toàn hơn này mới cho cô nhận ra rằng đó sẽ không phải là một hành trình đơn giản.<br><br>Con thì xanh lá? Con thì xanh dương? Ngay cả những con kì nhông màu hồng hiếm cũng bơi được? Bất kì chúng cũng có thể gây chết người.<br><br>Một sai lầm sẽ gây tử vong. Vậy nên cô nhặt một ngọn giáo đã bị vứt đi.<br><br>Một trận chiến sắp bắt đầu. Trên những hòn đảo sắt thép, bầu trời nhuộm sắc vàng cam, và những sinh vật kì lạ, kì ảo bay lượn.<br><br>Một con trông giống như mực màu trắng đang bay gần đó, tò mò về Nimbus đang bối rối. Chẳng mấy chốc, nhiều con mực trắng và còn có cả đen tụ tập lại, tiếng vo ve từ những chiếc cánh của chúng tạo nên một điệp khúc. Ở xa, một con rết cánh xanh lá quay tròn giữa không trung.<br><br>Thoát khỏi cơn mưa, Nimbus không thể bay tiếp được nữa. Nhưng cô không thấy lo lắm. Vượt qua thêm vài hòn đảo bay nữa cô sẽ đến nơi.<br><br>Cô có thể nhìn thấy nó–chỉ là có hơi xa–một công trình khổng lồ thuộc về một vị thần ngẫu nhiên nào đó. Những công trình thượng tầng khác ở quá xa để cô có thể nắm bắt. Chỗ này sẽ là đích đến cuối cùng.<br><br>Và thế là, cô ôm chặt lấy một con mực.<br><br>Việc bay qua những hòn đảo bay sẽ không mất lâu đâu. Những con thằn lằn xanh đuổi theo cô, nhưng rồi nhanh chóng chuyển sang con mồi gần đó. Tình đồng đội của chúng khiến cô thích thú.<br><br>Cuối cùng, cô trèo lên một bức tường cao chót vót của một công trình thượng tầng, thả con mực đã kiệt sức ra. Cô bắt vài con batflies cho người bạn mới quen của mình. Họ cùng nhau thưởng thức bữa ăn.<br><br>Chia tay rời đi, Nimbus bước vào cổng cao chót vót. Ở bên kia là bóng tối.<br><br>Trên những bức tường mà cô đi qua, hai tượng nhân cổ xưa quay mặt đối diện nhau, ngồi trong sự tôn sùng. Những bức tranh ấy không còn ý nghĩa gì trong cô nữa.<br><br>Cô chỉ là một người đưa tin. Cô đến để thực hiện những mong muốn còn lại của Deactivated Monsoon.<br><br>Còn đâu đều đã bị lãng quên.<br><br><br><br><br><br>Một vài người giám sát đỏ đã ra hiệu từ lâu rằng Nimbus đã vào nhầm nơi rồi, nhưng sự hiện diện của họ cũng có nghĩa là cô đã thu hút sự chú ý của vị thần này.<br><br>Khi cô đi qua những hành lang tăm tối của công trình, một vài khúc cua và ngã rẽ hiện ra những ánh đèn nhấp nháy.<br><br>Không, đó không phải lửa. Dưới bầu ánh sáng xanh, một khu chợ nhộn nhịp trải dài trước mắt cô, chiếu sáng sự u ám.<br><br>Cô nhận ra rằng công trình thượng tầng này đã bị một số cư dân chiếm lấy. Những sinh vật thông minh đã xâm nhập được vào, vượt qua hệ thống phòng thủ. Những vũ khí mà chúng nắm giữ xác nhận sự nghi ngờ của cô.<br><br>Và tệ hơn nữa, chúng là kẻ thù.<br><br>Nhận ra điều này, Nimbus quay người và bỏ chạy.<br><br>Những hình thù trông giống khỉ giương vũ khí của chúng và đuổi theo sau cô.<br><br>Những con vật ăn xác thối.<br><br>Cô vừa mới vào trong kiến trúc thì những kẻ xâm nhập đã đuổi cô ra ngoài. Iterator phải tính toán được một đáp án mà có thể giải thoát cho mọi sinh vật sống khỏi tai ương của cơn mưa. Đây luôn là nhiệm vụ duy nhất của chúng tôi.<br><br>Tôi chưa bao giờ tìm được ra cách để đạt được khẳng định ba lần ấy.<br><br>Nhưng trong lần mô phỏng cuối cùng của tôi, tôi đã bỏ qua những hạn chế do người trước để lại.<br><br>Tôi không biết điều này có nghĩa là gì. Nhưng mỗi khi nghĩ về cách Sliver of Straw có thể đã đi đến cùng một kết luận trước khi bị phá hủy (hoặc tự hủy), tôi vô cùng thấy bất an - nếu tôi thực sự cần thức ăn, nó sẽ khiến tôi mất cảm giác thèm ăn.<br><br>Và… Việc một người vẫn còn bất tỉnh hoặc tỉnh dậy không phải là quyết định do tôi.<br><br>Cấu trúc của tôi sẽ không tồn tại đủ lâu để tìm ra câu trả lời đúng, tôi cũng không biết những khám phá của mình sẽ tác động như nào đến những người theo chủ nghĩa của. Những quyết định nào họ sẽ đưa ra dựa trên những điều này?<br><br><br><br><br><br>Nimbus–Tôi đã đặt tên cho bạn như vậy. Xin lỗi vì đã làm choáng ngợp nhận thức của bạn với đống kiến thức này ngay sau khi trao cho bạn Dấu Hiệu của Sự Giao Tiếp.<br><br>Cảm ơn bạn đã đóng góp cho ống dẫn của chúng tôi trong suốt thời gian qua.<br><br>Bây giờ, tôi ủy thác cho bạn một nhiệm vụ cuối cùng.<br><br>Giao viên ngọc trai vàng này cho Epoch of Clouds or No Significant Harassment - hai Iterators gần gũi mà tôi tin tưởng. Bạn không cần phải hiểu nội dung của viên ngọc trai, nhưng tôi tin rằng… Đặc biệt đối với Epoch of Clouds, thông điệp của nó có thể mang lại chút an ủi.<br><br><br><br><br><br>Đây sẽ là một hành trình dài và gian khổ. Đừng ngoảnh lại. Hãy quên đi sự tồn tại của tôi. Một khi bạn rời khỏi nơi này, tôi sẽ không còn là một phần trong cuộc sống vĩnh hằng của bạn nữa. Điều đó có lẽ là tốt nhất–cho cả hai chúng ta.<br><br>Tôi ước rằng đó cũng có thể là điều tốt cho bạn, nữa.</p><h3 id="notanote" tabindex="-1">Notanote <a class="header-anchor" href="#notanote" aria-label="Permalink to &quot;Notanote&quot;">​</a></h3><ul><li><a href="#contents">Contents</a></li></ul><p>Cô ấy nghe thấy tiếng mưa rơi trước khi mở mắt.<br><br>Cô gái trẻ chớp mắt, mắt cô vẫn còn mờ, căn phòng tờ mờ sáng, ánh nắng ban mai không chiếu lọt qua kẽ hở tấm rèm trong phòng như mọi khi vào mỗi buổi sáng.<br><br>&quot;Bên ngoài trời đang mưa sao…?&quot;<br><br>Sự thanh bình, thanh thản, tiếng ồn trắng tĩnh lặng thường mang đến cho cô cảm giác thoải mái nhất nay có vẻ hơi khác thường. Nó im lặng một cách chết chóc.<br><br>Cô bước ra khỏi giường, chiếc áo phông ngoại cỡ tuột khỏi vai, đôi chân trần chạm vào sàn nhà lạnh lẽo. Cô dùng chân khều đôi dép của mình, phát hiện cả hai chiếc dép đều được giấu dưới gầm giường. Cô không nhớ mình đã để chúng đúng cách trước khi đi ngủ hay có lẽ là do một vài chú yêu tinh nhỏ tinh nghịch dưới gầm giường đang giở trò.<br><br>Bông bao bọc lấy chân cô, cảm giác ấm áp từ từ lan lên trên, cô xỏ đôi dép vào phòng tắm. Đứng trước gương, mái tóc hồng của cô rối tung sau khi thức dậy. Cô ngáp vài phát, lấy một chiếc lược để chải tóc. Chiếc lược chải vào từng sợi tóc, tạo ra những âm thanh xào xạc nhỏ, và chỉ một lúc tóc cô lại mượt mà trở lại. Cô gái ấy nhặt vài sợi tóc rơi, thở dài. Cô ném tóc vào thùng rác, vặn vòi nước , hơi nước bay dần dần lên gương.<br><br>“Ưm… Tóc rụng nhiều hơn nữa rồi,” cô gái lại thở dài. Có lẽ là do dạo gần đây chất lượng giấc ngủ của cô ấy kém. Sau khi sửa soạn lại mái tóc, gội đầu, và vỗ nhẹ khuôn mặt, cô gái cuối cùng cũng gọi to: “Note ơi ?”<br><br>Im lặng.<br><br>&quot;Note…?&quot;<br><br>Vẫn không có phản hồi gì.<br><br>Nếu là những ngày khác, Note sẽ nhảy từ đâu ra, ôm cô thật chặt từ phía sau và vui vẻ nói chào buổi sáng. Cô gái bắt đầu cảm thấy bối rối, một cảm giác bất an dâng lên trong cô. Cô bước ra khỏi phòng tắm hướng tới phòng của Note. Cô đẩy cửa mở. Căn phòng trống rỗng. Sạch sẽ như chưa từng có ai ở đây.<br><br>Cô ấy hoảng sợ.<br><br>Cô lục lọi khắp phòng, hét tên Note, hy vọng tìm thấy cô ấy đang trốn trong một chiếc tủ nhỏ, chờ thời điểm hoàn hảo để nhảy ra ngoài và làm cô ấy giật mình. Cô ấy thích trốn ở những nơi như vậy, những chiếc tủ nhỏ đến mức ngay cả một con mèo cũng không vừa.<br><br>Không có ai. Không có ai ở trong phòng ngủ. Cô gái lao ra ngoài tìm kiếm, gần như khiến cho cả ngôi nhà bị lộn tùng phèo. Không. Không. <i>Không.</i> Cô ấy không thể tìm thấy Note ở đâu cả. Không phải dưới gầm giường trong phòng ngủ, không phải trong lò nướng trong bếp, không phải dưới bồn rửa trong phòng tắm. Cô ấy có thể ở chỗ quái nào được nhỉ ?<br><br>Một ý nghĩ chợt thoáng qua tâm trí cô. <i>Nếu không ở trong nhà, thì chắc chắn cô ấy đang ở bên ngoài.</i> Không nghĩ nhiều, cô gái lo lắng này xỏ giày ở lối vào, mở cửa lao luôn ra ngoài, hoàn toàn không để ý đến trời đang mưa. Tiếng nước mưa lấp đầy xung quanh không khí. Nó tiếp tục rơi. Rơi một cách không thương tiếc. Những giọt nước lạnh như băng làm ướt đẫm cô gái, biến suy nghĩ của cô thành một vũng nước. Mưa rơi dày đặc trong không khí cùng với bóng tối bao trùm xung quanh cô. Những hạt mưa rơi xuống người cô, khiến cô cảm thấy hơi nhức nhối, cho đến khi một hạt lớn rơi xuống mí mắt, khiến cô nhận ra mình đã nhắm chặt mắt.<br><br>Rồi cô ấy mở mắt ra lần nữa. Sau đó - cô ấy được chào đón bởi một cảnh tượng khiến cô ấy bị xịt keo cứng ngắc ngang. Những tòa nhà cao chọc trời, giống như những khu rừng thép, mọc lên từ mặt nước - những cây sậy làm bằng bê tông và thép, có lẽ vậy. Những tòa nhà sừng sững ấy, chìm trong nước, như thể cả thế giới đã biến thành một vùng nước đọng lại.<br><br>Giật hết cả mình, cô bước lùi lại, chỉ khi vấp phải thứ gì đó trên mặt đất, khiến cô ngã lăn quay bò xoài. Cô thấy mình đang ở trên sân thượng của một tòa nhà, đường ống làm mát bị ăn mòn khiến cho cô bị ngã. Cú va chạm đó đã làm vỡ đường ống, hơi nước nóng bắn ra tùm lum kèm theo tiếng rít chói tai.<br><br>&quot;Mình… mình đang ở đây vậy ?&quot;|<br><br>Mưa rơi làm ướt sũng người cô một cách không thương tiếc, làm ướt đẫm bộ quần áo cô vừa mới chỉn chu gọn gàng. Cô ngồi dưới mưa, khép nép đầu gối gọn gàng lại vào trong ngực, cảm thấy sự bất lực và lạc lõng, cô chỉ muốn khóc.<br><br>&quot;Nota? Chào - buổi sáng nha !&quot; Một giọng nói phát ra từ phía sau cô, giống như chủ nhân ôm lấy cô, vui vẻ chào cô như mọi khi.<br><br>&quot;Cậu ngồi dưới mưa làm gì vậy ?&quot; Cô ấy hỏi.<br><br>&quot;Không… Note? Cậu cũng đang làm gì ở đây thế ?&quot;<br><br>&quot;Ủa ? Chẳng phải bình thường mọi thứ đã như thế này rồi hả ? Tớ đã luôn ở bên cạnh cậu mà. Nào, đứng dậy đi, kẻo lại bị cảm lạnh mất !&quot; Note lấy ra một chiếc ô lớn, che chắn cho cả hai khỏi cơn mưa dữ dội. May thay, cơn mưa như trút nước ấy đã che đi những giọt nước mắt sắp rơi xuống của cô, che chắn không cho Note biết.<br><br>Note giúp Nota đứng lên, và rồi họ nhận ra quần áo của họ đều ướt sũng.<br><br>&quot;Nhìn cậu kìa! Trông cậu ướt như chuột lột vậy!&quot; Note cười.<br><br>&quot;Ơ kìa? Nhìn lại bản thân cậu đê. Đầu tóc của cậu trông như cây chổi lau nhà ý,&quot; Nota đáp trả.<br><br>Hai người họ cười khùng điên, cơn mưa rơi như trút nước này cũng dịu dần đi khi có sự hiện diện của hai bọn họ.<br><br>Note đưa Nota vào trong tòa nhà, đóng chặt cửa lại, để ngăn chặn tiếng mưa. Tiếng mưa rơi ồn ào ngay lập tức chỉ còn lại là những tiếng vo ve nhỏ nhẹ.<br><br>&quot;Tớ không ngờ lại gặp cậu khi đang sửa máy sưởi trên sân thượng,&quot; Note nhận xét.<br><br>&quot;Tớ không ngờ… cậu ở đây… à không ý tớ là, chỗ này là đâu vậy?&quot; Nota cố gắng chuyển hướng sang chuyện khác, bỗng nhận ra rằng cô ấy là người làm hỏng lò sưởi và phải chịu trách nhiệm sửa nó.<br><br>&quot;Ở đấy á? Hmm… đại loại là một &#39;Thế giới không màu&#39;, họ gọi nó là vậy. Tớ cũng khá là bất ngờ khi đến đây. Còn lại thì, cậu nên tự mình khám phá thêm.&quot;<br><br>Đi sau Note, Nota đi theo xuống dưới hai tầng. Hành lang gần như bị bỏ hoang, ngổn ngang toàn mảnh vụn chất đống, không hề có một dấu vết nào của con người. Nếu như suy nghĩ về những lợi ích, cô nhận ra mình hoàn toàn không cần phải lo lắng về tiền nhà nữa, nếu nó vẫn có giá trị nào đó.<br><br>Nhận thấy có một chiếc bình nhựa nằm chắn ngang hành lang, Nota chợt nhận ra mọi thứ nhìn thấy ở đây đều mang màu đen và trắng. Dù hoa văn trên chiếc bình đó đã phai màu, nhưng đáng nhẽ không nên chuyển sang màu trắng đen mới phải. Liếc nhìn những bức tranh phong cảnh xếp trên các hộp cát tông gần đó, cô thấy nó cũng đơn sắc không kém.<br><br>&quot;Note, tại sao mọi thứ lại đều… đen và trắng vậy?&quot; Nota hỏi.<br><br>&quot;Ờm thì… dù sao đây cũng là &#39;thế giới không màu&#39;. Không hề có màu sắc, giống như những gì đang diễn ra vậy thôi,&quot; Note trả lời.<br><br>Mặc dù Note trông có vẻ chấp nhận cái cảnh tượng zô tri như này, nhưng thật sự đây là lần đầu tiên họ thấy một thế giới thực sự không có tí màu sắc nào cả.<br><br>Đến gần một cánh cửa, chẳng có gì thú vị về nó cả. Không có bảng tên, không có đồ trang trí độc đáo. Chỉ đơn giản là một cánh cửa gỗ, với một tay nắm kim loại đơn giản. Nota sực nhớ lại, đây là căn phòng duy nhất trong tòa nhà có cửa đóng kín được.<br><br>&quot;Cảm thấy tò mò về những gì đằng sau cánh cửa đúng khum ?&quot; Note cười một cách tinh nghịch.<br><br>&quot;…Mong là không có thứ gì đáng sợ…&quot;<br><br>&quot;À quên không bảo cậu. Nếu cậu muốn biết là gì, thì cậu tự mở cửa đi.&quot; Nhanh trí, Note chạy ra phía sau Nota, đẩy cô về gần cánh cửa. Nota nuốt nước bọt ừng ực toát lên vẻ lo lắng, đưa tay ra, nắm lấy tay nắm cửa, và vặn nó sang bên phải. Cánh cửa được mở ra, một khung cảnh phía sau cánh cửa hiện lên.<br><br>Trước khi có bất cứ điều gì khác, một cơn gió ập mạnh vào họ. Nơi đáng nhẽ phải có một dãy tường kính, thì chẳng có gì khác ngoài sự trống rỗng. Những thiệt hại bất thường ở các cạnh, và trần nhà thì bị mất, như thể có một thứ gì đó không thể diễn tả nổi ngoạm mất một miếng của căn phòng.<br><br>Và trong căn phòng này, còn có một người lạ. Có một cô gái đứng ở mép rìa, mà không có tấm kính nào chắn mưa. Mưa rơi thẳng xuống rìa căn phòng, mưa rơi làm ướt mặt đất, thế nhưng cô gái ấy dường như chẳng hề lay chuyển. Cô đứng ở đó, quần áo tung bay trong gió, mái tóc trắng của cô bay bổng. Nota đến gần hơn, ngạc nhiên khi thấy cơn mưa to tầm tã như vậy mà chẳng hề làm cô bị ướt sũng.<br><br>&quot;Cô gái đó…?&quot; Nota buột miệng nói.<br><br>&quot;Không chỉ có cô ấy. Nhìn xung quanh mà xem&quot;<br><br>Được Note nhắc nhở, cô nhìn xung quanh và thấy một cô gái khác đang ngồi xổm trong góc phòng. Mặc một trang phục màu đen, trắng và xám với đôi tai đặc biệt mà chỉ có ở động vật thuộc họ mèo, &quot;cô ta&quot; ngồi bên cạnh một ngôi nhà tạm bợ nhỏ được ghép từ ván gỗ, vải bạt, miếng sắt, và băng keo.<br><br>&quot;A ngôi nhà đó! Tớ đã xây nó đấy. Khá là tuyệt, đúng không?&quot; Nota không quan tâm mấy về cái cấu trúc kì lạ này, đặc biệt là khi sự chú ý của cô chỉ hướng đến hai cô gái còn lại.<br><br>Đến gần với đứa trẻ đang ngồi xổm. Nota cúi xuống và hỏi nhẹ nhàng,&quot;Ừm… cậu có… biết chuyện gì đang xảy ra ở đây không ?&quot;<br><br>Họ nhìn chằm chằm xuống sàn mà chẳng trả lời.<br><br>&quot;Ừ…&quot; Nota cảm thấy khó xử. &quot;Vậy… ờm… tên bạn là gì?&quot;<br><br>&quot;Kino.&quot; Lần này, cô nhận được câu trả lời rõ ràng từ <i>anh ấy</i>, mặc dù hầu như không thể nghe rõ.<br><br>&quot;Kino… đó là một cái tên hay đấy.&quot; Nota cố gắng để bắt chuyện với em ấy, nhưng dường như Kino không có hứng thú.<br><br>Bất kể cô có nói gì tiếp theo, Kino cũng chả trả lời thêm.<br><br>Đường cùng lắm, cô quyết định thử cách khác để tiếp cận. Nota bước tới chỗ cô gái đang đứng dưới mưa, tiếng gió gào thét khiến cô có chút sợ hãi. Cô cảm thấy như thể bị cơn gió nhấc bổng lên không trung bất cứ lúc nào, nhưng cô gái ở trước mặt cô vẫn không hề lay chuyển.<br><br>&quot;… Xin lỗi! Ừm, bạn tên là gì vậy?&quot; Nota phải hét lên thật to để đảm bảo giọng nói của cô không bị cơn bão lấn át.<br><br>Cô gái quay mặt về phía Nota, vẻ mặt vô hồn và cứng nhắc, như một con búp bê.<br><br>&quot;Tôi… không hiểu,&quot; búp bê đó nói, đôi mắt xanh thẳm nhìn vào Nota.<br><br>&quot;Cái… cái tên… là…?&quot; Nota bối rối trước cô gái bí ẩn ấy. &quot;Đó là, ừm… cách mà mọi người gọi cậu…&quot;<br><br>&quot;…Xin lỗi.&quot;<br><br>Cuộc trò chuyện đi vào ngõ cụt. Một lần nữa. Nota không bao giờ ngờ mình lại rơi vào trường hợp bế tắc như thế này. Trong khi đó, Note đứng bên cạnh, dường như chẳng mấy ngạc nhiên.<br><br>&quot;Tớ cũng đã hỏi tương tự khi tìm thấy họ,&quot; Note bảo vậy. &quot;Chẳng có gì xảy ra cả. À… không hẳn là <i>không có gì</i>. Sau khi theo dõi họ khoảng chừng, ờm, một… hai… ba… bốn… năm…,&quot; cô đếm, &quot;– năm tháng, rõ ràng <i>Kino</i> là nhân vật trung tâm của thế giới này.”<br><br>&quot;Hể… Ý cậu &#39;trung tâm&#39; là sao…?&quot; Nota đang hy vọng vào suy luận kiểu Sherlock nào đó, cách mà họ ghép một số manh mối và điểm đặc biệt lại để ra một kết quả bất ngờ. Nhưng thay vào đó, Nota chỉ thằng thừng đưa ra kết luận.<br><br>&quot;Vậy. Cậu không nhận ra có gì khiến cho Kino khác biệt so với chúng ta sao ?&quot;<br><br>&quot;Điều gì… khiến em ấy trở nên khác biệt ?&quot;<br><br>&quot;Nhìn kĩ vào mà xem. Em ý cũng <i>không màu</i> giống như thế giới này đó!&quot;<br><br>&quot;À… Tớ tưởng…&quot;<br><br>&quot;Cậu nghĩ đó chỉ là phong cách thời trang mà em ý chọn sao? Tất nhiên là không rồi, ngốc ghê!&quot;<br><br>&quot;Ồ… Tớ hiểu… – a… hắt xì!&quot; Đột nhiên cảm thấy ớn lạnh len lỏi vào trong người, Nota nhận ra do mình bị dính mưa. Cảnh tượng siêu chân thực lúc trước khiến cô mất cảm nhận về các giác quan của cơ thể, và giờ có nguy cơ bị cảm lạnh.<br><br>&quot;Ưm… Tớ sẽ đi đốt củi để làm khô người. Quần áo của cậu gần như có thể nhìn xuyên qua rồi.&quot; Tất nhiên, Note đang trêu chọc cô ấy. Với hai lớp bảo vệ từ áo khoác và áo sơ mi, không thể nào mà quần áo có thể trong suốt đến thế. Mặc dù vậy, Nota vẫn thắt chặt quần áo lại theo bản năng. Ngọn lửa đơn sắc bùng cháy, kêu tanh tách khẽ khàng. Sự kết hợp của ván gỗ và vải sợi protein khi bị đốt tỏa ra một mùi không mấy dễ chịu chút nào, nhưng ít nhất thì cũng làm khô quần áo của cả hai người.<br><br>Ngồi bên cạnh đống lửa, Nota dựa vào gần đó, cảm nhận hơi ấm. Ngồi bên cạnh cô, nhìn chằm chằm vào ngọn lửa đang kêu tanh tách.<br><br>&quot;Nè, Note, cậu nghĩ chúng ta sẽ mắc kẹt ở trong thế giới này bao lâu ?&quot; Nota hỏi.<br><br>&quot;Hông bít nữa, có lẽ là cả đời.&quot; Note trả lời, chọc ghẹo.<br><br>&quot;…Không, có lẽ nào chính là nó.&quot;<br><br>&quot;Cậu thực sự tin là vậy sao?&quot; Note cười khúc khích. &quot;Tớ đoán là một khi chúng ta giải quyết được gốc rễ của vấn đề, thì sẽ quay trở lại được !&quot;<br><br>&quot;Đấy là cậu đoán thế… chính bản thân cậu còn không chắc cơ mà !&quot;<br><br>&quot;–Dù sao thì! Tớ đoán mọi chuyện nó là như vậy đó! Phải có điều gì đó đáng để mong đợi, cậu biết mà&quot;<br><br>&quot;Ờ… ừm! Tớ không muốn nghĩ thêm gì về nó nữa đâu! Có quá nhiều thứ tồi tệ xảy ra trong ngày! Tớ <i>rất</i> là đói nè. Hay chúng ta cùng nấu gì đó để ăn đi.&quot; Nota tình nguyện ra phụ trách về bữa ăn của họ. Nhưng ngay khi cô nói ra điều đó, cô lại phải đối mặt với một vấn đề khác. Ở chỗ nào <i>trong</i> cái tòa nhà này mà cô có thể tìm được nguyên liệu? Không có một nơi nào trông giống như có thể tìm được thực phẩm tươi sống. Cô nhìn vào mép khe hở và không chắc có thể thấy rõ được đó có phải rêu bám trên nền bê tông–vẫn có mỗi màu đen trắng, nhưng rõ ràng là chất hữu cơ.<br><br>Cô gái đó ở một bên lặng lẽ đứng dậy, nhìn về phía xa. Nota tự hỏi không biết được cô ấy đang nhìn cái gì, hoặc là đang nghĩ cái gì. Cô gái mà trông giống búp bê nghe thấy tiếng động xào xạc gần đó, quay đầu lại, và thấy Nota đang thu thập địa y mọc trên bê tông.<br><br>Nota để ý thấy cô ấy đang nhìn mình và tỏ ra có chút xấu hổ. &quot;Ờm… mình đang kiếm đồ ăn ?&quot;<br><br>&quot;‘Đồ ăn’ là gì?&quot;<br><br>&quot;Đồ ăn… đồ ăn là thứ gì đó bạn có thể ăn để làm no bụng.&quot;<br><br>&quot;Bạn có thể làm no bụng bằng thứ này không?&quot; cô gái hỏi, tay cầm cục gạch lên.<br><br>&quot;Ờ… cái đó có lẽ… không ăn được… ?&quot; Nota vội vàng chạy ra ngăn cản cô lại vì cô trông như thực sự muốn cho cả cục gạch đó vào mồm. <i>Quả đúng là một cô gái kì lạ.</i> Nota tự nhủ.<br><br>Nota cho địa y vào trong một cái lon mà cô tìm được và đi lang thang khắp các phòng khác, để thu thập thêm nấm. Tuy nhiên, vì không thể phân biệt được màu sắc nên cô chỉ chọn những cái nào mà nhìn trông có vẻ an toàn–<i>có lẽ</i> là an toàn.<br><br>Ngọn lửa vẫn đang cháy và Nota đã nhờ Note dựng một chiếc giá đỡ tạm thời. Họ hứng nước mưa vào trong cái can để rửa thức ăn. Thứ mà không thiếu ở nơi này là <i>nước</i>. Họ đặt chiếc lon lên giá đỡ, để ngọn lửa đun nước. Khi nước bắt đầu sôi sùng sục lên và bốc hơi, Nota cẩn thận đặt lon xuống và múc thức ăn đã chín ra bốn cái bát, dùng một cái <i>thìa ăn món tráng miệng</i> để làm một cái muôi tạm thời.<br><br>Note hào phóng nhận lấy bát súp và ăn rất vui vẻ. Cô gái trong mưa đó bưng lên một bát, vẻ mặt ngơ ngác nhìn bát canh bị những hạt mưa rơi vào đó, bắn văng tùm lum. Nota phải giải thích chô cô cách để ăn và cách dùng thìa.<br><br>Còn lại, cô mang bát súp đến cho Kino. Cô ngồi xổm xuống và đặt bát súp vào tay Kino. Kino ngước mắt lên nhìn cô. Nota mỉm cười, ra hiệu bảo anh ý nên ăn thử ngay khi còn nóng. Kino cẩn thận nâng bát lên, nhấp một ngụm.<br><br>&quot;Nó… ấm quá.&quot;<br><br>&quot;Ha, tất nhiên rồi. Ngon lắm phải không?&quot;<br><br>&quot;…Ưm.&quot;<br><br>&quot; Phù… tốt rồi… Mình đã lo là nó sẽ có vị rất tệ…&quot;<br><br>&quot;Nó ngon lắm. Đã phải… rất lâu rồi mình mới được ăn thứ nào ngon như thế này…&quot;<br><br>&quot;Ra là thế… vậy, cậu đã ở đây bao lâu rồi ?&quot;<br><br>&quot;…không biết được… Tớ đã ở đây lâu lắm rồi…&quot;<br><br>&quot;À, Thì ra là vậy…&quot;<br><br>Kino im lặng và nhấp từng ngụm súp nóng hổi trên tay. Mặc dù họ chưa thể nói đến bất cứ chuyện gì quan trọng, nhưng thật sự việc Kino sẵn sàng nói chuyện lúc này là một khích lệ rất lớn đối với Nota.<br><br>Bên ngoài trời mưa không ngớt, nhưng ở trong căn phòng, được sưởi ấm nhờ ngọn lửa kêu tanh tách và những bát súp nóng hổi này.<br><br>Nota quay lại chỗ đống lửa, định nhấp một ngụm súp nóng hổi trong nồi, thì bỗng Note kéo cô ra ngoài.<br><br>&quot;Ê, cậu có thấy Kino có gì đó thay đổi không?&quot;<br><br>&quot;Hở? Thay đổi cái gì cơ ?&quot;<br><br>&quot;Cậu không nhận ra gì sao, phải không? Cậu nãy còn ngồi trước mặt anh ý cơ mà. Có vẻ như mọi người nói quan sát quá gần sẽ không thấy sự thay đổi nào đúng không ?&quot;<br><br>&quot;Bớt bớt dùm cái, rồi thay đổi chỗ nào ?&quot;<br><br>&quot;Khi nãy cậu đưa súp cho anh ý ăn, thế mà cậu không hề để có có màu sắc nhấp nháy xung quanh người anh ta sao ?&quot;<br><br>&quot;Thật hả…? Tớ không hề để ý luôn đó…&quot;<br><br>&quot;Chà, nếu cậu bỏ lỡ rồi, thì cậu đã bỏ lỡ nó. Nhưng nhờ đó, mà tớ có một ý tưởng, và tớ cần cậu thu nhập những vật liệu này giúp tớ…&quot; Note viết ra một danh sách dài liệt kê ra những món đồ, dài đến mức mà phải ghi hẳn ra giấy.<br><br>Sau một thời gian, Nota đã thu thập đủ những thứ mà Note yêu cầu, chất đầy trong chiếc túi nặng. Khi Nota đưa túi cho Note, Note đang mày mò với các dụng cụ.<br><br>&quot;Quao, cậu tìm được mọi thứ nhanh hơn như những gì tớ nghĩ! Có vẻ cậu có năng khiếu trong việc đi nhặt que gậy, nhỉ?&quot;<br><br>&quot;Có cần thiết phải khen tớ như vậy không? Tớ còn chẳng thể nào phân biệt được màu sắc của những thứ đó. Nó khó chịu lắm luôn ý, cậu biết mà.&quot;<br><br>&quot;Nhưng cậu vẫn tìm được mọi thứ.&quot; Note lấy cái túi, lục lọi các vật liệu kì lạ và bắt tay vào làm việc.&quot;Nghỉ ngơi đi. Cả ngày nay cậu quá bận rộn rồi.&quot;<br><br>&quot;Ồ… được thôi.&quot; Nota tìm một chỗ sạch sẽ ở cạnh tường và ngồi xuống. Ngoài trời cơn mưa rơi nhẹ nhàng, hơi ấm của ngọn lửa bập bùng dần dần đi vào trong cơ thể cô, ru vào một giấc ngủ đột ngột. Cuối cùng, Nota bị gọi dậy. &quot;Dậy đi !&quot; Đó chính là giọng nói của Note. &quot;Tớ làm được rồi !&quot;<br><br>&quot;Hả…? Gì cơ…?&quot;<br><br>Một cái hộp lớn đẩy đến trước mặt Nota. &quot;Đây nè. Tớ tốn rất nhiều công sức để làm ra đó.&quot;<br><br>Nota hoài nghi nhìn chiếc hộp. &quot;Liệu nó có thật sự hoạt động không…?&quot;<br><br>&quot;Phải thử mới biết được, đúng không? Trong trường hợp tệ nhất, là chúng ta sẽ bị kẹt ở đây mãi mãi.&quot;<br><br>&quot;Có vẻ như cậu đang đặt hết kì vọng vào nó…&quot;<br><br>&quot;Được rồi, được rồi, nếu mà không hoạt động thì tớ sẽ đổ lỗi hết tất cả là do cậu ám lời nguyền lên nó. Giờ thì, mang thứ này lên trên mái nhà giùm và thiết lập nó trong khi tớ đi bắt Kino và cô gái kia.&quot;<br><br>&quot;Thứ này có chống được nước không ?&quot;<br><br>&quot;Tớ đã nghĩ đến điều đó rồi, khỏi lo. Nó không thể bị phá hỏng do một cơn mưa nhỏ được!&quot;<br><br>&quot;Ờ…&quot;<br><br>Cái hộp đó quá lớn để Nota có thể vác đi hoặc bê lên, nên cuối cùng Note đẩy nó lên trên sân thượng. Mở ô ra, cô đẩy chiếc hộp vào một khoảng trống. Cũng vừa đúng lúc, ba người kia cũng lên tới nơi.<br><br>&quot;Được rồi đó–&quot; Note hét vào Nota, vẫy tay.<br><br>&quot;Thứ này thực sự hoạt động sao…?&quot; Nota lại lẩm bẩm một mình. Nhưng tại điểm này, cô thấy rằng họ không còn gì để mất nữa.<br><br>Nota lấy ra cái bật lửa. Đốt ngòi châm.<br><br><i>Xì…</i><br><br>Ngòi được châm lửa. Một làn khói bốc lên, theo sau là tiếng mưa nhịp nhàng.<br><br>&quot;Ủa gì vậy? Nó có bị hỏng không ta…? Đáng nhẽ ra nó phải…&quot;<br><br>Nota cúi xuống kiểm tra thì có một tia sáng lóa lên từ chiếc hộp.<br><br><i>Á—BÙM!!!</i><br><br>Nota giật mình và ngã lăn ra đất. Pháo hoa nổ tung trên trời.<br><br>Nota và Note đều chuyển sự chú ý sang Kino.<br><br>&quot;…?&quot; Kino nhìn lên bầu trời có vô vàn những quả pháo hoa. tiếng nổ đùng đùng vẫn còn vang vọng, khi nhiều quả pháo lần lượt được phóng lên.<br><br>&quot;Pháo hoa…&quot; Kino nhẹ nhàng thì thầm. &quot;Đẹp quá…&quot;<br><br>Pháo hoa trên bầu trời bỗng chợt đổi màu. Màu sắc rực rỡ tạo thành những bông hoa nở rộ, một bông rồi lại bông khác, tạo nên một cảnh tượng độc đáo mà chỉ dành cho bốn người họ.<br><br>Chiếc ô của Kino rơi xuống đất. Note đi nhặt nó lên, chợt thấy cơn mưa đang tạnh dần và cảm giác những giọt mưa lạnh lẽo trở nên ấm áp hơn, như thể mùa xuân đang đến gần.<br><br>&quot;Note, nhìn kìa!&quot; Màu sắc của những cái cây đó! Chúng đã có màu trở lại !&quot; Nota chỉ về phía những dây leo bám bên tường tòa nhà.<br><br>&quot;Có vẻ như chúng thật sự hoạt động rồi.&quot;<br><br>&quot;Đúng vậy! Và… ồ, cơ thể của cậu… cũng đang trở nên phai mờ…&quot;<br><br>&quot;…Của cậu cũng vậy. Có phải chúng ta sắp rời đi sao? Tớ chỉ vừa mới tận hưởng nơi này thôi mà.&quot;<br><br>&quot;Cậu không thể nghiêm túc tí được à.&quot;<br><br>Note cười thầm. &quot;Đùa tí. Chẳng phải quay trở lại thế giới của chúng ta vẫn tốt hơn sao?&quot;<br><br>&quot;Ừ… chắc là vậy&quot;<br><br>&quot;Và tớ tin là đã tới lúc phải rời xa hai người bạn của chúng ta.&quot;<br><br>&quot;Phải rồi…&quot;<br><br>Nota và Note vẫn tay chào tạm biệt Kino và cô gái kia.<br><br>Khoảng khắc tiếp theo, Nota thấy mình đang nằm ở trên vỉa hè trước cửa nhà họ, cùng với việc bị Note đè lên người.<br><br>&quot;Cậu sẽ đè chết tớ mất! Xê ra coi!&quot; Nota kháng cự.<br><br>&quot;Xin lỗi, Tớ không có ý làm như vậy.&quot;<br><br>&quot;Quần áo của tớ… chúng lại ướt sạch rồi…&quot;<br><br>&quot;Nhanh lên, vào trong nhà và thay đồ đi kẻo lại bị ốm đấy.&quot;<br><br>Dòng nước nóng chảy ra từ vòi hoa sen, xua tan cái lạnh và sự mệt mỏi. Nota đột nhiên nhớ ra rằng cô còn chẳng được nếm thử món súp. Nhưng xét theo nụ cười lần cuối của Kino, thì chắc hẳn nó rất ngon, phải không?<br><br>Ngoài trời, mưa đã ngừng rơi.</p>',35))])}const e=a(o,[["render",y]]);export{C as __pageData,e as default};
